INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-major-charm-potion', 'consumable', 'Major Charm Potion', NULL, NULL, '{"description":"You gain a +1 bonus to your Presence until your next rest."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-major-enlighten-potion', 'consumable', 'Major Enlighten Potion', NULL, NULL, '{"description":"You gain a +1 bonus to your Knowledge until your next rest."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-blood-of-the-yorgi', 'consumable', 'Blood of the Yorgi', NULL, NULL, '{"description":"You can drink this blood to disappear from where you are and immediately reappear at a point you can see within very Far range."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-hornet-s-secret-potion', 'consumable', 'Hornet’s Secret Potion', NULL, NULL, '{"description":"After drinking this potion, the next successful attack you make critically succeeds."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-redthorn-saliva', 'consumable', 'Redthorn Saliva', NULL, NULL, '{"description":"You can apply this saliva to a weapon that deals physical damage to add a d12 to your next damage roll with that weapon."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-channelstone', 'consumable', 'Channelstone', NULL, NULL, '{"description":"You can use this stone to take a spell or grimoire from your vault, use it once, and return it to your vault."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-mythic-dust', 'consumable', 'Mythic Dust', NULL, NULL, '{"description":"You can apply this dust to a weapon that deals magic damage to add a d12 to your next damage roll with that weapon."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-acidpaste', 'consumable', 'Acidpaste', NULL, NULL, '{"description":"This paste eats away walls and other surfaces in bright flashes."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-hopehold-flare', 'consumable', 'Hopehold Flare', NULL, NULL, '{"description":"When you use this flare, allies within Close range roll a d6 when they spend a Hope. On a result of 6, they gain the effect of that Hope without spending it. The flare lasts until the end of the scene."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-major-arcane-shard', 'consumable', 'Major Arcane Shard', NULL, NULL, '{"description":"You can make a Finesse Roll to throw this shard at a group of adversaries within Far range. Targets you succeed against take 4d20 magic damage."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-featherbone', 'consumable', 'Featherbone', NULL, NULL, '{"description":"You can use this bone to control your falling speed for a number of minutes equal to your level."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-circle-of-the-void', 'consumable', 'Circle of the Void', NULL, NULL, '{"description":"Mark a Stress to create a void that extends up to Far range. No magic can be cast inside the void, and creatures within the void are immune to magic damage."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-sun-tree-sap', 'consumable', 'Sun Tree Sap', NULL, NULL, '{"description":"Consume this sap to roll a d6. On a result of 5–6, clear 2 HP. On a result of 2–4, clear 3 Stress. On a result of 1, see through the veil of death and return unscathed, gaining one scar."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-dripfang-poison', 'consumable', 'Dripfang Poison', NULL, NULL, '{"description":"A creature who consumes this poison takes 2d10 direct magic damage."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-major-health-potion', 'consumable', 'Major Health Potion', NULL, NULL, '{"description":"Clear 1d4+2 HP."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-major-stamina-potion', 'consumable', 'Major Stamina Potion', NULL, NULL, '{"description":"Clear 1d4+2 Stress."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-ogre-musk', 'consumable', 'Ogre Musk', NULL, NULL, '{"description":"You can use this musk to prevent anyone from tracking you by mundane or magical means until your next rest."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-wingsprout', 'consumable', 'Wingsprout', NULL, NULL, '{"description":"You gain magic wings that allow you to fly for a number of minutes equal to your level."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-jar-of-lost-voices', 'consumable', 'Jar of Lost Voices', NULL, NULL, '{"description":"You can open this jar to release a deafening echo of voices for a number of minutes equal to your Instinct. Creatures within Far range unprepared for the sound take 6d8 magic damage."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-dragonbloom-tea', 'consumable', 'Dragonbloom Tea', NULL, NULL, '{"description":"You can drink this tea to unleash a fiery breath attack. Make an Instinct Roll against all adversaries in front of you within Close range. Targets you succeed against take 2d20 physical damage using your Proficiency."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-bridge-seed', 'consumable', 'Bridge Seed', NULL, NULL, '{"description":"Thick vines grow from your location to a point of your choice within Far range, allowing you to climb up or across them. The vines dissipate on your next short rest."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-sleeping-sap', 'consumable', 'Sleeping Sap', NULL, NULL, '{"description":"You can drink this potion to fall asleep for a full night’s rest. You clear all Stress upon waking."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-feast-of-xurla', 'consumable', 'Feast of Xurla', NULL, NULL, '{"description":"You can eat this meal to clear all HP and Stress and gain 1d4 Hope."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-bonding-honey', 'consumable', 'Bonding Honey', NULL, NULL, '{"description":"This honey can be used to glue two objects together permanently."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-shrinking-potion', 'consumable', 'Shrinking Potion', NULL, NULL, '{"description":"You can drink this potion to halve your size until you choose to drop this form or your next rest. While in this form, you have a +2 bonus to Agility and a –1 penalty to your Proficiency."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-growing-potion', 'consumable', 'Growing Potion', NULL, NULL, '{"description":"You can drink this potion to double your size until you choose to drop this form or your next rest. While in this form, you have a +2 bonus to Strength and a +1 bonus to your Proficiency."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-knowledge-stone', 'consumable', 'Knowledge Stone', NULL, NULL, '{"description":"If you die while holding this stone, an ally can take a card from your loadout to place in their loadout or vault. After they take this knowledge, the stone crumbles."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-sweet-moss', 'consumable', 'Sweet Moss', NULL, NULL, '{"description":"You can consume this moss during a rest to clear 1d10 HP or 1d10 Stress."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-blinding-orb', 'consumable', 'Blinding Orb', NULL, NULL, '{"description":"You can activate this orb to create a flash of bright light. All targets within Close range become Blinded until they mark HP."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-death-tea', 'consumable', 'Death Tea', NULL, NULL, '{"description":"After you drink this tea, you instantly kill your target when you critically succeed on an attack. If you don’t critically succeed on an attack before your next long rest, you die."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-mirror-of-marigold', 'consumable', 'Mirror of Marigold', NULL, NULL, '{"description":"When you take damage, you can spend a Hope to negate that damage, after which the mirror shatters."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('consumable-stardrop', 'consumable', 'Stardrop', NULL, NULL, '{"description":"You can use this stardrop to summon a hailstorm of comets that deals 8d20 physical damage to all targets within Very Far range."}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-acid-burrower', 'adversary', 'Acid Burrower', NULL, 1, '{"description":"A horse-sized insect with digging claws and acidic blood.","motives":"Burrow, drag away, feed, reposition","difficulty":"14","thresholds":"8/15","hp":"8","stress":"3","attack":{"modifier":"+3","name":"Claws","range":"Very Close","damage":"1d12+2 phy"},"features":[{"name":"Relentless (3) - Passive","text":"The Burrower can be spotlighted up to three times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Earth Eruption - Action","text":"Mark a Stress to have the Burrower burst out of the ground. All creatures within Very Close range must succeed on an Agility Reaction Roll or be knocked over, making them Vulnerable until they next act."},{"name":"Spit Acid - Action","text":"Make an attack against all targets in front of the Burrower within Close range. Targets the Burrower succeeds against take 2d6 physical damage and must mark an Armor Slot without receiving its benefits (they can still use armor to reduce the damage). If they can’t mark an Armor Slot, they must mark an additional HP and you gain a Fear."},{"name":"Acid Bath - Reaction","text":"When the Burrower takes Severe damage, all creatures within Close range are bathed in their acidic blood, taking 1d10 physical damage. This splash covers the ground within Very Close range with blood, and all creatures other than the Burrower who move through it take 1d6 physical damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-bear', 'adversary', 'Bear', NULL, 1, '{"description":"A large bear with thick fur and powerful claws.","motives":"Climb, defend territory, pummel, track","difficulty":"14","thresholds":"9/17","hp":"7","stress":"2","attack":{"modifier":"+1","name":"Claws","range":"Melee","damage":"1d8+3 phy"},"features":[{"name":"Overwhelming Force - Passive","text":"Targets who mark HP from the Bear’s standard attack are knocked back to Very Close range."},{"name":"Bite - Action","text":"Mark a Stress to make an attack against a target within Melee range. On a success, deal 3d4+10 physical damage and the target is Restrained until they break free with a successful Strength Roll."},{"name":"Momentum - Reaction","text":"When the Bear makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-cave-ogre', 'adversary', 'Cave Ogre', NULL, 1, '{"description":"A massive humanoid who sees all sapient life as food.","motives":"Bite off heads, feast, rip limbs, stomp, throw enemies","difficulty":"13","thresholds":"8/15","hp":"8","stress":"3","attack":{"modifier":"+1","name":"Club","range":"Very Close","damage":"1d10+2 phy"},"features":[{"name":"Ramp Up - Passive","text":"You must spend a Fear to spotlight the Ogre. While spotlighted, they can make their standard attack against all targets within range."},{"name":"Bone Breaker - Passive","text":"The Ogre’s attacks deal direct damage."},{"name":"Hail of Boulders - Action","text":"Mark a Stress to pick up heavy objects and throw them at all targets in front of the Ogre within Far range. Make an attack against these targets. Targets the Ogre succeeds against take 1d10+2 physical damage. If they succeed against more than one target, you gain a Fear."},{"name":"Rampaging Fury - Reaction","text":"When the Ogre marks 2 or more HP, they can rampage. Move the Ogre to a point within Close range and deal 2d6+3 direct physical damage to all targets in their path."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-construct', 'adversary', 'Construct', NULL, 1, '{"description":"A roughly humanoid being of stone and steel, assembled and animated by magic.","motives":"Destroy environment, serve creator, smash target, trample groups","difficulty":"13","thresholds":"7/15","hp":"9","stress":"4","attack":{"modifier":"+4","name":"Fist Slam","range":"Melee","damage":"1d20 phy"},"features":[{"name":"Relentless (2) - Passive","text":"The Construct can be spotlighted up to two times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Weak Structure - Passive","text":"When the Construct marks HP from physical damage, they must mark an additional HP."},{"name":"Trample - Action","text":"Mark a Stress to make an attack against all targets in the Construct’s path when they move. Targets the Construct succeeds against take 1d8 physical damage."},{"name":"Overload - Reaction","text":"Before rolling damage for the Construct’s attack, you can mark a Stress to gain a +10 bonus to the damage roll. The Construct can then take the spotlight again."},{"name":"Death Quake - Reaction","text":"When the Construct marks their last HP, the magic powering them ruptures in an explosion of force. Make an attack with advantage against all targets within Very Close range. Targets the Construct succeeds against take 1d12+2 magic damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-courtier', 'adversary', 'Courtier', NULL, 1, '{"description":"An ambitious and ostentatiously dressed socialite.","motives":"Discreet, gain favor, maneuver, scheme","difficulty":"12","thresholds":"4/8","hp":"3","stress":"4","attack":{"modifier":"-4","name":"Daggers","range":"Melee","damage":"1d4+2 phy"},"features":[{"name":"Mockery - Action","text":"Mark a Stress to say something mocking and force a target within Close range to make a Presence Reaction Roll (14) to see if they can save face. On a failure, the target must mark 2 Stress and is Vulnerable until the scene ends."},{"name":"Scapegoat - Action","text":"Spend a Fear and target a PC. The Courtier convinces a crowd or prominent individual that the target is the cause of their current conflict or misfortune."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-deeproot-defender', 'adversary', 'Deeproot Defender', NULL, 1, '{"description":"A burly vegetable-person with grasping vines.","motives":"Ambush, grab, protect, pummel","difficulty":"10","thresholds":"8/14","hp":"7","stress":"3","attack":{"modifier":"+2","name":"Vines","range":"Close","damage":"1d8+3 phy"},"features":[{"name":"Ground Slam - Action","text":"Slam the ground, knocking all targets within Very Close range back to Far range. Each target knocked back by this must mark a Stress."},{"name":"Grab and Drag - Action","text":"Make an attack against a target within Close range. On a success, spend a Fear to pull them into Melee range, deal 1d6+2 physical damage, and Restrain them until the Defender takes Severe damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-dire-wolf', 'adversary', 'Dire Wolf', NULL, 1, '{"description":"A large wolf with menacing teeth, seldom encountered alone.","motives":"Defend territory, harry, protect pack, surround, trail","difficulty":"12","thresholds":"5/9","hp":"4","stress":"3","attack":{"modifier":"+2","name":"Claws","range":"Melee","damage":"1d6+2 phy"},"features":[{"name":"Pack Tactics - Passive","text":"If the Wolf makes a successful standard attack and another Dire Wolf is within Melee range of the target, deal 1d6+5 physical damage instead of their standard damage and you gain a Fear."},{"name":"Hobbling Strike - Action","text":"Mark a Stress to make an attack against a target within Melee range. On a success, deal 3d4+10 direct physical damage and make them Vulnerable until they clear at least 1 HP."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-giant-mosquitoes', 'adversary', 'Giant Mosquitoes', NULL, 1, '{"description":"Dozens of fist-sized mosquitoes, flying together for protection.","motives":"Fly away, harass, steal blood","difficulty":"10","thresholds":"5/9","hp":"6","stress":"3","attack":{"modifier":"-2","name":"Proboscis","range":"Melee","damage":"1d8+3 phy"},"features":[{"name":"Horde (1d4+1) - Passive","text":"When the Mosquitoes have marked half or more of their HP, their standard attack deals 1d4+1 physical damage instead."},{"name":"Flying - Passive","text":"While flying, the Mosquitoes have a +2 bonus to their Difficulty."},{"name":"Bloodseeker - Reaction","text":"When the Mosquitoes’ attack causes a target to mark HP, you can mark a Stress to force the target to mark an additional HP."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-giant-rat', 'adversary', 'Giant Rat', NULL, 1, '{"description":"A cat-sized rodent skilled at scavenging and survival.","motives":"Burrow, hunger, scavenge, wear down","difficulty":"10","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"-4","name":"Claws","range":"Melee","damage":"1 phy"},"features":[{"name":"Minion (3) - Passive","text":"The Rat is defeated when they take any damage. For every 3 damage a PC deals to the Rat, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Giant Rats within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 1 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-giant-scorpion', 'adversary', 'Giant Scorpion', NULL, 1, '{"description":"A human-sized insect with tearing claws and a stinging tail.","motives":"Ambush, feed, grapple, poison","difficulty":"13","thresholds":"7/13","hp":"6","stress":"3","attack":{"modifier":"+1","name":"Pincers","range":"Melee","damage":"1d12+2 phy"},"features":[{"name":"Double Strike - Action","text":"Mark a Stress to make a standard attack against two targets within Melee range."},{"name":"Venomous Stinger - Action","text":"Make an attack against a target within Very Close range. On a success, spend a Fear to deal 1d4+4 physical damage and Poison them until their next rest or they succeed on a Knowledge Roll (16). While Poisoned, the target must roll a d6 before they make an action roll. On a result of 4 or lower, they must mark a Stress."},{"name":"Momentum - Reaction","text":"When the Scorpion makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-glass-snake', 'adversary', 'Glass Snake', NULL, 1, '{"description":"A clear serpent with a massive head that leaves behind a glass shard trail wherever they go.","motives":"Climb, feed, keep distance, scare","difficulty":"14","thresholds":"6/10","hp":"5","stress":"3","attack":{"modifier":"+2","name":"Glass Fangs","range":"Very Close","damage":"1d8+2 phy"},"features":[{"name":"Armor-Shredding Shards - Passive","text":"On a successful attack within Melee range against the Snake, the attacker must mark an Armor Slot without receiving its benefits (they can still use armor to reduce the damage). If they can’t mark an Armor Slot, they must mark an additional HP."},{"name":"Spinning Serpent - Action","text":"Mark a Stress to make an attack against all targets within Very Close range. Targets the Snake succeeds against take 1d6+1 physical damage."},{"name":"Spitter - Action","text":"Spend a Fear to introduce a 6 Spitter Die. When the Snake is in the spotlight, roll this die. On a result of 5 or higher, all targets in front of the Snake within Far range must succeed on an Agility Reaction Roll or take 1d4 physical damage. The Snake can take the spotlight a second time this GM turn."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-harrier', 'adversary', 'Harrier', NULL, 1, '{"description":"A nimble fighter armed with javelins.","motives":"Flank, harry, kite, profit","difficulty":"12","thresholds":"5/9","hp":"3","stress":"3","attack":{"modifier":"+1","name":"Javelin","range":"Close","damage":"1d6+2 phy"},"features":[{"name":"Maintain Distance - Passive","text":"After making a standard attack, the Harrier can move anywhere within Far range."},{"name":"Fall Back - Reaction","text":"When a creature moves into Melee range to make an attack, you can mark a Stress before the attack roll to move anywhere within Close range and make an attack against that creature. On a success, deal 1d10+2 physical damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-archer-guard', 'adversary', 'Archer Guard', NULL, 1, '{"description":"A tall guard bearing a longbow and quiver with arrows fletched in the settlement’s colors.","motives":"Arrest, close gates, make it through the day, pin down","difficulty":"10","thresholds":"4/8","hp":"3","stress":"2","attack":{"modifier":"+1","name":"Longbow","range":"Far","damage":"1d8+3 phy"},"features":[{"name":"Hobbling Shot - Action","text":"Make an attack against a target within Far range. On a success, mark a Stress to deal 1d12+3 physical damage. If the target marks HP from this attack, they have disadvantage on Agility Rolls until they clear at least 1 HP."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-bladed-guard', 'adversary', 'Bladed Guard', NULL, 1, '{"description":"An armored guard bearing a sword and shield painted in the settlement’s colors.","motives":"Arrest, close gates, make it through the day, pin down","difficulty":"12","thresholds":"5/9","hp":"5","stress":"2","attack":{"modifier":"+1","name":"Longsword","range":"Melee","damage":"1d6+1 phy"},"features":[{"name":"Shield Wall - Passive","text":"A creature who tries to move within Very Close range of the Guard must succeed on an Agility Roll. If additional Bladed Guards are standing in a line alongside the first, and each is within Melee range of another guard in the line, the Difficulty increases by the total number of guards in the line."},{"name":"Detain - Action","text":"Make an attack against a target within Very Close range. On a success, mark a Stress to Restrain the target until they break free with a successful attack, Finesse Roll, or Strength Roll."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-head-guard', 'adversary', 'Head Guard', NULL, 1, '{"description":"A seasoned guard with a mace, a whistle, and a bellowing voice.","motives":"Arrest, close gates, pin down, seek glory","difficulty":"15","thresholds":"7/13","hp":"7","stress":"3","attack":{"modifier":"+4","name":"Mace","range":"Melee","damage":"1d10+4 phy"},"features":[{"name":"Rally Guards - Action","text":"Spend 2 Fear to spotlight the Head Guard and up to 2d4 allies within Far range."},{"name":"On My Signal - Reaction","text":"Countdown (5). When the Head Guard is in the spotlight for the first time, activate the countdown. It ticks down when a PC makes an attack roll. When it triggers, all Archer Guards within Far range make a standard attack with advantage against the nearest target within their range. If any attacks succeed on the same target, combine their damage."},{"name":"Momentum - Reaction","text":"When the Head Guard makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-jagged-knife-bandit', 'adversary', 'Jagged Knife Bandit', NULL, 1, '{"description":"A cunning criminal in a cloak bearing one of the gang’s iconic knives.","motives":"Escape, profit, steal, throw smoke","difficulty":"12","thresholds":"8/14","hp":"5","stress":"3","attack":{"modifier":"+1","name":"Daggers","range":"Melee","damage":"1d8+1 phy"},"features":[{"name":"Climber - Passive","text":"The Bandit climbs just as easily as they run."},{"name":"From Above - Passive","text":"When the Bandit succeeds on a standard attack from above a target, they deal 1d10+1 physical damage instead of their standard damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-jagged-knife-hexer', 'adversary', 'Jagged Knife Hexer', NULL, 1, '{"description":"A staff-wielding bandit in a cloak adorned with magical paraphernalia, using curses to vex their foes.","motives":"Command, hex, profit","difficulty":"13","thresholds":"5/9","hp":"4","stress":"4","attack":{"modifier":"+2","name":"Staff","range":"Far","damage":"1d6+2 mag"},"features":[{"name":"Curse - Action","text":"Choose a target within Far range and temporarily Curse them. While the target is Cursed, you can mark a Stress so that target rolls with Hope to make the roll be with Fear instead."},{"name":"Chaotic Flux - Action","text":"Make an attack against up to three targets within Very Close range. Mark a Stress to deal 2d6+3 magic damage to targets the Hexer succeeded against."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-jagged-knife-kneebreaker', 'adversary', 'Jagged Knife Kneebreaker', NULL, 1, '{"description":"An imposing brawler carrying a large club.","motives":"Grapple, intimidate, profit, steal","difficulty":"12","thresholds":"7/14","hp":"7","stress":"4","attack":{"modifier":"-3","name":"Club","range":"Melee","damage":"1d4+6 phy"},"features":[{"name":"I’ve Got ‘Em - Passive","text":"Creatures Restrained by the Kneebreaker take double damage from attacks by other adversaries."},{"name":"Hold Them Down - Action","text":"Make an attack against a target within Melee range. On a success, the target takes no damage but is Restrained and Vulnerable. The target can break free, clearing both conditions, with a successful Strength Roll or is freed automatically if the Kneebreaker takes Major or greater damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-jagged-knife-lackey', 'adversary', 'Jagged Knife Lackey', NULL, 1, '{"description":"A thief with simple clothes and small daggers, eager to prove themselves.","motives":"Escape, profit, throw smoke","difficulty":"9","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"-2","name":"Daggers","range":"Melee","damage":"2 phy"},"features":[{"name":"Minion (3) - Passive","text":"The Lackey is defeated when they take any damage. For every 3 damage a PC deals to the Lackey, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Jagged Knife Lackeys within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 2 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-jagged-knife-lieutenant', 'adversary', 'Jagged Knife Lieutenant', NULL, 1, '{"description":"A seasoned bandit in quality leathers with a strong voice and cunning eyes.","motives":"Bully, command, profit, reinforce","difficulty":"13","thresholds":"7/14","hp":"6","stress":"3","attack":{"modifier":"+2","name":"Javelin","range":"Close","damage":"1d8+3 phy"},"features":[{"name":"Tactician - Action","text":"When you spotlight the Lieutenant, mark a Stress to also spotlight two allies within Close range."},{"name":"More Where That Came From - Action","text":"Summon three Jagged Knife Lackeys, who appear at Far range."},{"name":"Coup de Grace - Action","text":"Spend a Fear to make an attack against a Vulnerable target within Close range. On a success, deal 2d6+12 physical damage and the target must mark a Stress."},{"name":"Momentum - Reaction","text":"When the Lieutenant makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-jagged-knife-shadow', 'adversary', 'Jagged Knife Shadow', NULL, 1, '{"description":"A nimble scoundrel bearing a wicked knife and utilizing shadow magic to isolate targets.","motives":"Ambush, conceal, divide, profit","difficulty":"12","thresholds":"4/8","hp":"3","stress":"3","attack":{"modifier":"+1","name":"Daggers","range":"Melee","damage":"1d4+4 phy"},"features":[{"name":"Backstab - Passive","text":"When the Shadow succeeds on a standard attack that has advantage, they deal 1d6+6 physical damage instead of their standard damage."},{"name":"Cloaked - Action","text":"Become Hidden until after the Shadow’s next attack. Attacks made while Hidden from this feature have advantage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-jagged-knife-sniper', 'adversary', 'Jagged Knife Sniper', NULL, 1, '{"description":"A lanky bandit striking from cover with a shortbow.","motives":"Ambush, hide, profit, reposition","difficulty":"13","thresholds":"4/7","hp":"3","stress":"2","attack":{"modifier":"-1","name":"Shortbow","range":"Far","damage":"1d10+2 phy"},"features":[{"name":"Unseen Strike - Passive","text":"If the Sniper is Hidden when they make a successful standard attack against a target, they deal 1d10+4 physical damage instead of their standard damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-merchant', 'adversary', 'Merchant', NULL, 1, '{"description":"A finely dressed trader with a keen eye for financial gain.","motives":"Buy low and sell high, create demand, inflate prices, seek profit","difficulty":"12","thresholds":"4/8","hp":"3","stress":"3","attack":{"modifier":"-4","name":"Club","range":"Melee","damage":"1d4+1 phy"},"features":[{"name":"Preferential Treatment - Passive","text":"A PC who succeeds on a Presence Roll against the Merchant gains a discount on purchases. A PC who fails on a Presence Roll against the Merchant must pay more and has disadvantage on future Presence Rolls against the Merchant."},{"name":"The Runaround - Passive","text":"When a PC rolls a 14 or lower on a Presence Roll made against the Merchant, they must mark a Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-minor-chaos-elemental', 'adversary', 'Minor Chaos Elemental', NULL, 1, '{"description":"A coruscating mass of uncontrollable magic.","motives":"Confound, destabilize, transmogrify","difficulty":"14","thresholds":"7/14","hp":"7","stress":"3","attack":{"modifier":"+3","name":"Warp Blast","range":"Close","damage":"1d12+6 mag"},"features":[{"name":"Arcane Master - Passive","text":"The Elemental is resistant to magic damage."},{"name":"Sickening Flux - Action","text":"Mark a HP to force all targets within Close range to mark a Stress and become Vulnerable until their next rest or they clear a HP."},{"name":"Remake Reality - Action","text":"Spend a Fear to transform the area within Very Close range into a different biome. All targets within this area take 2d6+3 direct magic damage."},{"name":"Magical Reflection - Reaction","text":"When the Elemental takes damage from an attack within Close range, deal an amount of damage to the attacker equal to half of the damage they dealt."},{"name":"Momentum - Reaction","text":"When the Elemental makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-minor-fire-elemental', 'adversary', 'Minor Fire Elemental', NULL, 1, '{"description":"A living flame the size of a large bonfire.","motives":"Encircle enemies, grow in size, intimidate, start fires","difficulty":"13","thresholds":"7/15","hp":"9","stress":"3","attack":{"modifier":"+3","name":"Elemental Blast","range":"Far","damage":"1d10+4 mag"},"features":[{"name":"Relentless (2) - Passive","text":"The Elemental can be spotlighted up to two times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Scorched Earth - Action","text":"Mark a Stress to choose a point within Far range. The ground within Very Close range of that point immediately bursts into flames. All creatures within this area must make an Agility Reaction Roll. Targets who fail take 2d8 magic damage from the flames. Targets who succeed take half damage."},{"name":"Explosion - Action","text":"Spend a Fear to erupt in a fiery explosion. Make an attack against all targets within Close range. Targets the Elemental succeeds against take 1d8 magic damage and are knocked back to Far range."},{"name":"Consume Kindling - Reaction","text":"Three times per scene, when the Elemental moves on objects that are highly flammable, consume them to clear a HP or a Stress."},{"name":"Momentum - Reaction","text":"When the Elemental makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-minor-demon', 'adversary', 'Minor Demon', NULL, 1, '{"description":"A crimson-hued creature from the Circles Below, consumed by rage against all mortals.","motives":"Act erratically, corral targets, relish pain, torment","difficulty":"14","thresholds":"8/15","hp":"8","stress":"4","attack":{"modifier":"+3","name":"Claws","range":"Melee","damage":"1d8+6 phy"},"features":[{"name":"Relentless (2) - Passive","text":"The Demon can be spotlighted up to two times per GM turn. Spend Fear as usual to spotlight them."},{"name":"All Must Fall - Passive","text":"When a PC rolls a failure with Fear while within Close range of the Demon, they lose a Hope."},{"name":"Hellfire - Action","text":"Spend a Fear to rain down hellfire within Far range. All targets within the area must make an Agility Reaction Roll. Targets who fail take 1d20+3 magic damage. Targets who succeed take half damage."},{"name":"Reaper - Reaction","text":"Before rolling damage for the Demon’s attack, you can mark a Stress to gain a bonus to the damage roll equal to the Demon’s current number of marked HP."},{"name":"Momentum - Reaction","text":"When the Demon makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-minor-treant', 'adversary', 'Minor Treant', NULL, 1, '{"description":"An ambulatory sapling rising up to defend their forest.","motives":"Crush, overwhelm, protect","difficulty":"10","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"-2","name":"Cleaved Branch","range":"Melee","damage":"4 phy"},"features":[{"name":"Minion (5) - Passive","text":"The Treant is defeated when they take any damage. For every 5 damage a PC deals to the Treant, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Minor Treants within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 4 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-green-ooze', 'adversary', 'Green Ooze', NULL, 1, '{"description":"A moving mound of translucent green slime.","motives":"Camouflage, consume and multiply, creep up, envelop","difficulty":"8","thresholds":"5/10","hp":"5","stress":"2","attack":{"modifier":"+1","name":"Ooze Appendage","range":"Melee","damage":"1d6+1 mag"},"features":[{"name":"Slow - Passive","text":"When you spotlight the Ooze and they don’t have a token on their stat block, they can’t act. Place a token on their stat block and describe what they’re preparing to do. When you spotlight the Ooze and they have a token on their stat block, clear the token and they can act."},{"name":"Acidic Form - Passive","text":"When the Ooze makes a successful attack, the target must mark an Armor Slot without receiving its benefits (they can still use armor to reduce the damage). If they can’t mark an Armor Slot, they must mark an additional HP."},{"name":"Envelope - Action","text":"Make a standard attack against a target within Melee range. On a success, the Ooze envelops them and the target must mark 2 Stress. The target must mark an additional Stress when they make an action roll. If the Ooze takes Severe damage, the target is freed."},{"name":"Split - Reaction","text":"When the Ooze has 3 or more HP marked, you can spend a Fear to split them into two Tiny Green Oozes (with no marked HP or Stress). Immediately spotlight both of them."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-tiny-green-ooze', 'adversary', 'Tiny Green Ooze', NULL, 1, '{"description":"A small moving mound of translucent green slime.","motives":"Camouflage, creep up","difficulty":"14","thresholds":"4/None","hp":"2","stress":"1","attack":{"modifier":"-1","name":"Ooze Appendage","range":"Melee","damage":"1d4+1 mag"},"features":[{"name":"Acidic Form - Passive","text":"When the Ooze makes a successful attack, the target must mark an Armor Slot without receiving its benefits (they can still use armor to reduce the damage). If they can’t mark an Armor Slot, they must mark an additional HP."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-red-ooze', 'adversary', 'Red Ooze', NULL, 1, '{"description":"A moving mound of translucent flaming red slime.","motives":"Camouflage, consume and multiply, ignite, start fires","difficulty":"10","thresholds":"6/11","hp":"5","stress":"3","attack":{"modifier":"+1","name":"Ooze Appendage","range":"Melee","damage":"1d8+3 mag"},"features":[{"name":"Creeping Fire - Passive","text":"The Ooze can only move within Very Close range as their normal movement. They light any flammable object they touch on fire."},{"name":"Ignite - Action","text":"Make an attack against a target within Very Close range. On a success, the target takes 1d8 magic damage and is ignited until they’re extinguished with a successful Finesse Roll (14). While ignited, the target takes 1d4 magic damage when they make an action roll."},{"name":"Split - Reaction","text":"When the Ooze has 3 or more HP marked, you can spend a Fear to split them into two Tiny Red Oozes (with no marked HP or Stress). Immediately spotlight both of them."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-tiny-red-ooze', 'adversary', 'Tiny Red Ooze', NULL, 1, '{"description":"A small moving mound of translucent flaming red slime.","motives":"Blaze, camouflage","difficulty":"11","thresholds":"5/None","hp":"2","stress":"1","attack":{"modifier":"-1","name":"Ooze Appendage","range":"Melee","damage":"1d4+2 mag"},"features":[{"name":"Burning - Reaction","text":"When a creature within Melee range deals damage to the Ooze, they take 1d6 direct magic damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-petty-noble', 'adversary', 'Petty Noble', NULL, 1, '{"description":"A richly dressed and adorned aristocrat brimming with hubris.","motives":"Abuse power, gather resources, mobilize minions","difficulty":"14","thresholds":"6/10","hp":"3","stress":"5","attack":{"modifier":"-3","name":"Rapier","range":"Melee","damage":"1d6+1 phy"},"features":[{"name":"My Land, My Rules - Passive","text":"All social actions made against the Noble on their land have disadvantage."},{"name":"Guards, Seize Them! - Action","text":"Once per scene, mark a Stress to summon 1d4 Bladed Guards, who appear at Far range to enforce the Noble’s will."},{"name":"Exile - Action","text":"Spend a Fear and target a PC. The Noble proclaims that the target and their allies are exiled from the noble’s territory. While exiled, the target and their allies have disadvantage during social situations within the Noble’s domain."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-pirate-captain', 'adversary', 'Pirate Captain', NULL, 1, '{"description":"A charismatic sea dog with an impressive hat, eager to raid and plunder.","motives":"Command, make ''em walk the plank, plunder, raid","difficulty":"14","thresholds":"7/14","hp":"7","stress":"5","attack":{"modifier":"+1","name":"Cutlass","range":"Melee","damage":"1d12+2 phy"},"features":[{"name":"Swashbuckler - Passive","text":"When the Captain marks 2 or fewer HP from an attack within Melee range, the attacker must mark a Stress."},{"name":"Reinforcements - Action","text":"Once per scene, mark a Stress to summon a Pirate Raiders Horde, which appears at Far range."},{"name":"No Quarter - Action","text":"Spend a Fear to choose a target who has three or more Pirates within Melee range of them. The Captain leads the Pirates in hurling threats and promises of a watery grave. The target must make a Presence Reaction Roll. On a failure, the target marks 1d4+1 Stress. On a success, they must mark a Stress."},{"name":"Momentum - Reaction","text":"When the Captain makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-pirate-raiders', 'adversary', 'Pirate Raiders', NULL, 1, '{"description":"Seafaring scoundrels moving in a ravaging pack.","motives":"Gang up, plunder, overwhelm","difficulty":"12","thresholds":"5/11","hp":"4","stress":"3","attack":{"modifier":"+1","name":"Cutlass","range":"Melee","damage":"1d8+2 phy"},"features":[{"name":"Horde (1d4+1) - Passive","text":"When the Raiders have marked half or more of their HP, their standard attack deals 1d4+1 physical damage instead."},{"name":"Swashbuckler - Passive","text":"When the Raiders mark 2 or fewer HP from an attack within Melee range, the attacker must mark a Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-pirate-tough', 'adversary', 'Pirate Tough', NULL, 1, '{"description":"A thickly muscled and tattooed pirate with melon-sized fists.","motives":"Plunder, raid, smash, terrorize","difficulty":"13","thresholds":"8/15","hp":"5","stress":"3","attack":{"modifier":"+1","name":"Massive Fists","range":"Melee","damage":"2d6 phy"},"features":[{"name":"Swashbuckler - Passive","text":"When the Tough marks 2 or fewer HP from an attack within Melee range, the attacker must mark a Stress."},{"name":"Clear the Decks - Action","text":"Make an attack against a target within Very Close range. On a success, mark a Stress to move into Melee range of the target, dealing 3d4 physical damage and knocking the target back to Close range."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-sellsword', 'adversary', 'Sellsword', NULL, 1, '{"description":"An armed mercenary testing their luck.","motives":"Charge, lacerate, overwhelm, profit","difficulty":"10","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"+3","name":"Longsword","range":"Melee","damage":"3 phy"},"features":[{"name":"Minion (4) - Passive","text":"The Sellsword is defeated when they take any damage. For every 4 damage a PC deals to the Sellsword, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Sellswords within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 3 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-skeleton-archer', 'adversary', 'Skeleton Archer', NULL, 1, '{"description":"A fragile skeleton with a shortbow and arrows.","motives":"Perforate distracted targets, play dead, steal skin","difficulty":"9","thresholds":"4/7","hp":"3","stress":"2","attack":{"modifier":"+2","name":"Shortbow","range":"Far","damage":"1d8+1 phy"},"features":[{"name":"Opportunist - Passive","text":"When two or more adversaries are within Very Close range of a creature, all damage they each deal to that creature is doubled."},{"name":"Deadly Shot - Action","text":"Make an attack against a Vulnerable target within Far range. On a success, mark a Stress to deal 3d4+8 physical damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-skeleton-dredge', 'adversary', 'Skeleton Dredge', NULL, 1, '{"description":"A clattering pile of bones.","motives":"Fall apart, overwhelm, play dead, steal skin","difficulty":"8","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"-1","name":"Bone Claws","range":"Melee","damage":"1 phy"},"features":[{"name":"Minion (4) - Passive","text":"The Dredge is defeated when they take any damage. For every 4 damage a PC deals to the Dredge, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Dredges within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 1 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-skeleton-knight', 'adversary', 'Skeleton Knight', NULL, 1, '{"description":"A large armored skeleton with a huge blade.","motives":"Cut down the living, steal skin, wreak havoc","difficulty":"13","thresholds":"7/13","hp":"5","stress":"2","attack":{"modifier":"+2","name":"Rusty Greatsword","range":"Melee","damage":"1d10+2 phy"},"features":[{"name":"Terrifying - Passive","text":"When the Knight makes a successful attack, all PCs within Close range lose a Hope and you gain a Fear."},{"name":"Cut to the Bone - Action","text":"Mark a Stress to make an attack against all targets within Very Close range. Targets the Knight succeeds against take 1d8+2 physical damage and must mark a Stress."},{"name":"Dig Two Graves - Reaction","text":"When the Knight is defeated, they make an attack against a target within Very Close range (prioritizing the creature who killed them). On a success, the target takes 1d8+4 physical damage and loses 1d4 Hope."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-skeleton-warrior', 'adversary', 'Skeleton Warrior', NULL, 1, '{"description":"A dirt-covered skeleton armed with a rusted blade.","motives":"Feign death, gang up, steal skin","difficulty":"10","thresholds":"4/8","hp":"3","stress":"2","attack":{"modifier":"0","name":"Sword","range":"Melee","damage":"1d6+2 phy"},"features":[{"name":"Only Bones - Passive","text":"The Warrior is resistant to physical damage."},{"name":"Reform - Reaction","text":"When the Warrior is defeated, roll a d6. On a result of 6, if there are other adversaries on the battlefield, the Warrior re-forms with no marked HP."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-spellblade', 'adversary', 'Spellblade', NULL, 1, '{"description":"A mercenary combining swordplay and magic to deadly effect.","motives":"Blast, command, endure","difficulty":"14","thresholds":"8/14","hp":"6","stress":"3","attack":{"modifier":"+3","name":"Empowered Longsword","range":"Melee","damage":"1d8+4 phy/mag"},"features":[{"name":"Arcane Steel - Passive","text":"Damage dealt by the Spellblade’s standard attack is considered both physical and magic."},{"name":"Suppressing Blast - Action","text":"Mark a Stress and target a group within Far range. All targets must succeed on an Agility Reaction Roll or take 1d8+2 magic damage. You gain a Fear for each target who marked HP from this attack."},{"name":"Move as Unit - Action","text":"Spend 2 Fear to spotlight up to five allies within Far range."},{"name":"Momentum - Reaction","text":"When the Spellblade makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-swarm-of-rats', 'adversary', 'Swarm of Rats', NULL, 1, '{"description":"A skittering mass of ordinary rodents moving as one like a ravenous wave.","motives":"Consume, obscure, swarm","difficulty":"10","thresholds":"6/10","hp":"6","stress":"2","attack":{"modifier":"-3","name":"Claws","range":"Melee","damage":"1d8+2 phy"},"features":[{"name":"Horde (1d4+1) - Passive","text":"When the Swarm has marked half or more of their HP, their standard attack deals 1d4+1 physical damage instead."},{"name":"In Your Face - Passive","text":"All targets within Melee range have disadvantage on attacks against targets other than the Swarm."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-sylvan-soldier', 'adversary', 'Sylvan Soldier', NULL, 1, '{"description":"A faerie warrior adorned in armor made of leaves and bark.","motives":"Ambush, hide, overwhelm, protect, trail","difficulty":"11","thresholds":"6/11","hp":"4","stress":"2","attack":{"modifier":"0","name":"Scythe","range":"Melee","damage":"1d8+1 phy"},"features":[{"name":"Pack Tactics - Passive","text":"If the Soldier makes a standard attack and another Sylvan Soldier is within Melee range of the target, deal 1d8+5 physical damage instead of their standard damage."},{"name":"Forest Control - Action","text":"Spend a Fear to pull down a tree within Close range. A creature hit by the tree must succeed on an Agility Reaction Roll (15) or take 1d10 physical damage."},{"name":"Blend In - Reaction","text":"When the Soldier makes a successful attack, you can mark a Stress to become Hidden until the Soldier’s next attack or a PC succeeds on an Instinct Roll (14) to find them."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-tangle-bramble-swarm', 'adversary', 'Tangle Bramble Swarm', NULL, 1, '{"description":"A cluster of animated, blood-drinking tumbleweeds, each the size of a large gourd.","motives":"Digest, entangle, immobilize","difficulty":"12","thresholds":"6/11","hp":"6","stress":"3","attack":{"modifier":"0","name":"Thorns","range":"Melee","damage":"1d6+3 phy"},"features":[{"name":"Horde (1d4+2) - Passive","text":"When the Swarm has marked half or more of their HP, their standard attack deals 1d4+2 physical damage instead."},{"name":"Crush - Action","text":"Mark a Stress to deal 2d6+8 direct physical damage to a target with 3 or more bramble tokens."},{"name":"Encumber - Reaction","text":"When the Swarm succeeds on an attack, give the target a bramble token. If a target has any bramble tokens, they are Restrained. If a target has 3 or more bramble tokens, they are also Vulnerable. All bramble tokens can be removed by succeeding on a Finesse Roll (12 + the number of bramble tokens) or dealing Major or greater damage to the Swarm. If bramble tokens are removed from a target using a Finesse Roll, a number of Tangle Bramble Minions spawn within Melee range equal to the number of tokens removed."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-tangle-bramble', 'adversary', 'Tangle Bramble', NULL, 1, '{"description":"An animate, blood-drinking tumbleweed.","motives":"Consume, drain, entangle","difficulty":"11","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"-1","name":"Thorns","range":"Melee","damage":"2 phy"},"features":[{"name":"Minion (4) - Passive","text":"The Bramble is defeated when they take any damage. For every 4 damage a PC deals to the Tangle Bramble, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Tangle Brambles within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 2 physical damage each. Combine this damage."},{"name":"Drain and Multiply - Reaction","text":"When an attack from the Bramble causes a target to mark HP and there are three or more Tangle Bramble Minions within Close range, you can combine the Minions into a Tangle Bramble Swarm Horde. The Horde’s HP is equal to the number of Minions combined."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-weaponmaster', 'adversary', 'Weaponmaster', NULL, 1, '{"description":"A master-at-arms wielding a sword twice their size.","motives":"Act first, aim for the weakest, intimidate","difficulty":"14","thresholds":"8/15","hp":"6","stress":"3","attack":{"modifier":"+2","name":"Claymore","range":"Very Close","damage":"1d12+2 phy"},"features":[{"name":"Goading Strike - Action","text":"Make a standard attack against a target. On a success, mark a Stress to Taunt the target until their next successful attack. The next time the Taunted target attacks, they have disadvantage against targets other than the Weaponmaster."},{"name":"Adrenaline Burst - Action","text":"Once per scene, spend a Fear to clear 2 HP and 2 Stress."},{"name":"Momentum - Reaction","text":"When the Weaponmaster makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-brawny-zombie', 'adversary', 'Brawny Zombie', NULL, 1, '{"description":"A large corpse, decay-bloated and angry.","motives":"Crush, destroy, hurl debris, slam","difficulty":"10","thresholds":"8/15","hp":"7","stress":"4","attack":{"modifier":"+2","name":"Slam","range":"Very Close","damage":"1d12+3 phy"},"features":[{"name":"Slow - Passive","text":"When you spotlight the Zombie and they don’t have a token on their stat block, they can’t act yet. Place a token on their stat block and describe what they’re preparing to do. When you spotlight the Zombie and they have a token on their stat block, clear the token and they can act."},{"name":"Rend Asunder - Action","text":"Make a standard attack with advantage against a target the Zombie has Restrained. On a success, the attack deals direct damage."},{"name":"Rip and Tear - Reaction","text":"When the Zombies makes a successful standard attack, you can mark a Stress to temporarily Restrain the target and force them to mark 2 Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-young-dryad', 'adversary', 'Young Dryad', NULL, 1, '{"description":"An imperious tree-person leading their forest’s defenses.","motives":"Command, nurture, prune the unwelcome","difficulty":"11","thresholds":"6/11","hp":"6","stress":"2","attack":{"modifier":"0","name":"Scythe","range":"Melee","damage":"1d8+5 phy"},"features":[{"name":"Voice of the Forest - Action","text":"Mark a Stress to spotlight 1d4 allies within range of a target they can attack without moving. On a success, their attacks deal half damage."},{"name":"Thorny Cage - Action","text":"Spend a Fear to form a cage around a target within Very Close range and Restrain them until they’re freed with a successful Strength Roll. When a creature makes an action roll against the cage, they must mark a Stress."},{"name":"Momentum - Reaction","text":"When the Dryad makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-patchwork-zombie-hulk', 'adversary', 'Patchwork Zombie Hulk', NULL, 1, '{"description":"A towering gestalt of corpses moving as one, with torso-sized limbs and fists as large as a grown halfling.","motives":"Absorb corpses, flail, hunger, terrify","difficulty":"13","thresholds":"8/15","hp":"10","stress":"3","attack":{"modifier":"+4","name":"Too Many Arms","range":"Very Close","damage":"1d20 phy"},"features":[{"name":"Destructive - Passive","text":"When the Zombie takes Major or greater damage, they mark an additional HP."},{"name":"Flailing Limbs - Passive","text":"When the Zombie makes a standard attack, they can attack all targets within Very Close range."},{"name":"Another for the Pile - Action","text":"When the Zombie is within Very Close range of a corpse, they can incorporate it into themselves, clearing a HP and a Stress."},{"name":"Tormented Screams - Action","text":"Mark a Stress to cause all PCs within Far range to make a Presence Reaction Roll (13). Targets who fail lose a Hope and you gain a Fear for each. Targets who succeed must mark a Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-rotted-zombie', 'adversary', 'Rotted Zombie', NULL, 1, '{"description":"A decaying corpse ambling toward their prey.","motives":"Eat flesh, hunger, maul, surround","difficulty":"8","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"-3","name":"Bite","range":"Melee","damage":"2 phy"},"features":[{"name":"Minion (3) - Passive","text":"The Zombie is defeated when they take any damage. For every 3 damage a PC deals to the Zombie, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Rotted Zombies within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 2 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-shambling-zombie', 'adversary', 'Shambling Zombie', NULL, 1, '{"description":"An animated corpse that moves shakily, driven only by hunger.","motives":"Devour, hungry, mob enemy, shred flesh","difficulty":"10","thresholds":"4/6","hp":"4","stress":"1","attack":{"modifier":"0","name":"Bite","range":"Melee","damage":"1d6+1 phy"},"features":[{"name":"Too Many to Handle - Passive","text":"When the Zombie is within Melee range of a creature and at least one other Zombie is within Close range, all attacks against that creature have advantage."},{"name":"Horrifying - Passive","text":"Targets who mark HP from the Zombie’s attacks must also mark a Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-zombie-pack', 'adversary', 'Zombie Pack', NULL, 1, '{"description":"A group of shambling corpses instinctively moving together.","motives":"Consume flesh, hunger, maul","difficulty":"8","thresholds":"6/12","hp":"6","stress":"3","attack":{"modifier":"-1","name":"Bite","range":"Melee","damage":"1d10+2 phy"},"features":[{"name":"Horde (1d4+2) - Passive","text":"When the Zombies have marked half or more of their HP, their standard attack deals 1d4+2 physical damage instead."},{"name":"Overwhelm - Reaction","text":"When the Zombies mark HP from an attack within Melee range, you can mark a Stress to make a standard attack against the attacker."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-archer-squadron', 'adversary', 'Archer Squadron', NULL, 2, '{"description":"A group of trained archers bearing massive bows.","motives":"Stick together, survive, volley fire","difficulty":"13","thresholds":"8/16","hp":"4","stress":"3","attack":{"modifier":"0","name":"Longbow","range":"Far","damage":"2d6+3 phy"},"features":[{"name":"Horde (1d6+3) - Passive","text":"When the Squadron has marked half or more of their HP, their standard attack deals 1d6+3 physical damage instead."},{"name":"Focused Volley - Action","text":"Spend a Fear to target a point within Far range. Make an attack with advantage against all targets within Close range of that point. Targets the Squadron succeeds against take 1d10+4 physical damage."},{"name":"Suppressing Fire - Action","text":"Mark a Stress to target a point within Far range. Until the next roll with Fear, a creature who moves within Close range of that point must make an Agility Reaction Roll. On a failure, they take 2d6+3 physical damage. On a success, they take half damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-assassin-poisoner', 'adversary', 'Assassin Poisoner', NULL, 2, '{"description":"A cunning scoundrel skilled in both poisons and ambushing.","motives":"Anticipate, get paid, kill, taint food and water","difficulty":"14","thresholds":"8/16","hp":"4","stress":"4","attack":{"modifier":"+3","name":"Poisoned Throwing Dagger","range":"Close","damage":"2d8+1 phy"},"features":[{"name":"Grindeloth Venom - Passive","text":"Targets who mark HP from the Assassin’s attacks are Vulnerable until they clear a HP."},{"name":"Out of Nowhere - Passive","text":"The Assassin has advantage on attacks if they are Hidden."},{"name":"Fumigation - Action","text":"Drop a smoke bomb that fills the air within Close range with smoke, Dizzilying all targets in this area. Dizzied targets have disadvantage on their next action roll, then clear the condition."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-apprentice-assassin', 'adversary', 'Apprentice Assassin', NULL, 2, '{"description":"A young trainee eager to prove themselves.","motives":"Act reckless, kill, prove their worth, show off","difficulty":"13","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"-1","name":"Thrown Dagger","range":"Very Close","damage":"4 phy"},"features":[{"name":"Minion (6) - Passive","text":"The Assassin is defeated when they take any damage. For every 6 damage a PC deals to the Assassin, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Apprentice Assassins within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 4 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-master-assassin', 'adversary', 'Master Assassin', NULL, 2, '{"description":"A seasoned killer with a threatening voice and a deadly blade.","motives":"Ambush, get out alive, kill, prepare for all scenarios","difficulty":"15","thresholds":"12/25","hp":"7","stress":"5","attack":{"modifier":"+5","name":"Serrated Dagger","range":"Close","damage":"2d10+2 phy"},"features":[{"name":"Won’t See It Coming - Passive","text":"The Assassin deals direct damage while they’re Hidden."},{"name":"Strike as One - Action","text":"Mark a Stress to spotlight a number of other Assassins equal to the Assassin’s unmarked Stress."},{"name":"The Subtle Blade - Reaction","text":"When the Assassin successfully makes a standard attack against a Vulnerable target, you can spend a Fear to deal Severe damage instead of their standard damage."},{"name":"Momentum - Reaction","text":"When the Assassin makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-battle-box', 'adversary', 'Battle Box', NULL, 2, '{"description":"A cube-shaped construct with a different rune on each of their six sides.","motives":"Change tactics, trample foes, wait in disguise","difficulty":"15","thresholds":"10/20","hp":"8","stress":"6","attack":{"modifier":"+2","name":"Slam","range":"Melee","damage":"2d6+3 phy"},"features":[{"name":"Relentless (2) - Passive","text":"The Box can be spotlighted up to two times per turn. Spend Fear as usual to spotlight them."},{"name":"Randomized Tactics - Action","text":"Mark a Stress and roll a d6. The Box uses the corresponding move: 1. Mana Beam: The Box fires a searing beam. Make an attack against a target within Far range. On a success, deal 2d10+2 magic damage. 2. Fire Jets: The Box shoots into the air, spinning and releasing jets of flame. Make an attack against all targets within Close range. Targets the Box succeeds against take 2d8 physical damage. 3. Trample: The Box rockets around erratically. Make an attack against all PCs within Close range. Targets the Box succeeds against take 1d6+5 physical damage and are Vulnerable until their next roll with Hope. 4. Shocking Gas: The Box sprays out a silver gas sparking with lightning. All targets within Close range must succeed on a Finesse Reaction Roll or mark 3 Stress. 5. Stunning Clap: The Box leaps and their sides clap, creating a concussive boom. All targets within Very Close range must succeed on a Strength Reaction Roll or become Vulnerable until the cube is defeated. 6. Psonic Whine: The Box releases a cluster of mechanical bees whose buzz rattles mortal minds. All targets within Close range must succeed on a Presence Reaction Roll or take 2d4+9 direct magic damage."},{"name":"Overcharge - Reaction","text":"Before rolling damage for the Box’s attack, you can mark a Stress to add a d6 to the damage roll. Additionally, you gain a Fear."},{"name":"Death Quake - Reaction","text":"When the Box marks their last HP, the magic powering them ruptures in an explosion of force. All targets within Close range must succeed on an Instinct Reaction Roll or take 2d8+1 magic damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-chaos-skull', 'adversary', 'Chaos Skull', NULL, 2, '{"description":"A floating humanoid skull animated by scintillating magic.","motives":"Cackle, consume magic, serve creator","difficulty":"15","thresholds":"8/16","hp":"5","stress":"4","attack":{"modifier":"+2","name":"Energy Blast","range":"Close","damage":"2d8+3 mag"},"features":[{"name":"Levitation - Passive","text":"The Skull levitates several feet off the ground and can’t be Restrained."},{"name":"Wards - Passive","text":"The Skull is resistant to magic damage."},{"name":"Magic Burst - Action","text":"Mark a Stress to make an attack against all targets within Close range. Targets the Skull succeeds against take 2d6+4 magic damage."},{"name":"Siphon Magic - Action","text":"Spend a Fear to make an attack against a PC with a Spellcast trait within Very Close range. On a success, the target marks 1d4 Stress and the Skull clears that many Stress. Additionally, on a success, the Skull can immediately be spotlighted again."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-conscript', 'adversary', 'Conscript', NULL, 2, '{"description":"A poorly trained civilian pressed into war.","motives":"Follow orders, gang up, survive","difficulty":"12","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"0","name":"Spears","range":"Very Close","damage":"6 phy"},"features":[{"name":"Minion (6) - Passive","text":"The Conscript is defeated when they take any damage. For every 6 damage a PC deals to the Conscript, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Conscripts within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 6 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-courtesan', 'adversary', 'Courtesan', NULL, 2, '{"description":"An accomplished manipulator and master of the social arts.","motives":"Entice, maneuver, secure patrons","difficulty":"13","thresholds":"7/13","hp":"3","stress":"4","attack":{"modifier":"-3","name":"Dagger","range":"Melee","damage":"1d4+3 phy"},"features":[{"name":"Searing Glance - Reaction","text":"When a PC within Close range makes a Presence Roll, you can mark a Stress to cast a gaze toward the aftermath. On the target’s failure, they must mark 2 Stress and are Vulnerable until the scene ends or they succeed on a social action against the Courtesan. On the target’s success, they must mark a Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-cult-adept', 'adversary', 'Cult Adept', NULL, 2, '{"description":"An experienced mage wielding shadow and fear.","motives":"Curry favor, hinder foes, uncover knowledge","difficulty":"14","thresholds":"9/18","hp":"4","stress":"6","attack":{"modifier":"+2","name":"Rune-Covered Rod","range":"Far","damage":"2d4+3 mag"},"features":[{"name":"Enervating Blast - Action","text":"Spend a Fear to make a standard attack against a target within range. On a success, the target must mark a Stress."},{"name":"Shroud of the Fallen - Action","text":"Mark a Stress to wrap an ally within Close range in a shroud of Protection until the Adept marks their last HP. While Protected, the target has resistance to all damage."},{"name":"Shadow Shackles - Action","text":"Spend a Fear and choose a point within Far range. All targets within Close range of that point are Restrained in smoky chains until they break free with a successful Strength or Instinct Roll. A target Restrained by this feature must spend a Hope to make an action roll."},{"name":"Fear Is Fuel - Reaction","text":"Twice per scene, when a PC rolls a failure with Fear, clear a Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-cult-fang', 'adversary', 'Cult Fang', NULL, 2, '{"description":"A professional killer-turned-cultist.","motives":"Capture sacrifices, isolate prey, rise in the ranks","difficulty":"15","thresholds":"9/17","hp":"4","stress":"4","attack":{"modifier":"+2","name":"Long Knife","range":"Melee","damage":"2d8+4 phy"},"features":[{"name":"Shadow’s Embrace - Passive","text":"The Fang can climb and walk on vertical surfaces. Mark a Stress to move from one shadow to another within Far range."},{"name":"Pick Off the Straggler - Action","text":"Mark a Stress to cause a target within Melee range to make an Instinct Reaction Roll. On a failure, the target must mark 2 Stress and is teleported with the Fang to a shadow within Far range, making them temporarily Vulnerable. On a success, the target must mark a Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-cult-initiate', 'adversary', 'Cult Initiate', NULL, 2, '{"description":"A low-ranking cultist in simple robes, eager to gain power.","motives":"Follow orders, gain power, seek forbidden knowledge","difficulty":"13","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"0","name":"Ritual Dagger","range":"Melee","damage":"5 phy"},"features":[{"name":"Minion (6) - Passive","text":"The Initiate is defeated when they take any damage. For every 6 damage a PC deals to the Initiate, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Cult Initiates within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 5 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-demonic-hound-pack', 'adversary', 'Demonic Hound Pack', NULL, 2, '{"description":"Unnatural hounds lit from within by hellfire.","motives":"Cause fear, consume flesh, please masters","difficulty":"15","thresholds":"11/23","hp":"6","stress":"3","attack":{"modifier":"0","name":"Claws and Fangs","range":"Melee","damage":"2d8+2 phy"},"features":[{"name":"Horde (2d4+1) - Passive","text":"When the Pack has marked half or more of their HP, their standard attack deals 2d4+1 physical damage instead."},{"name":"Dreadhowl - Action","text":"Mark a Stress to make all targets within Very Close range lose a Hope. If a target is not able to lose a Hope, they must instead mark 2 Stress."},{"name":"Momentum - Reaction","text":"When the Pack makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-electric-eels', 'adversary', 'Electric Eels', NULL, 2, '{"description":"A swarm of eels that encircle and electrocute.","motives":"Avoid larger predators, shock prey, tear apart","difficulty":"14","thresholds":"10/20","hp":"5","stress":"3","attack":{"modifier":"0","name":"Shocking Bite","range":"Melee","damage":"2d6+4 phy"},"features":[{"name":"Horde (2d4+1) - Passive","text":"When the Eels have marked half or more of their HP, their standard attack deals 2d4+1 physical damage instead."},{"name":"Paralyzing Shock - Action","text":"Mark a Stress to make a standard attack against all targets within Very Close range. You gain a Fear for each target that marks HP."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-elite-soldier', 'adversary', 'Elite Soldier', NULL, 2, '{"description":"An armored squire or experienced commoner looking to advance.","motives":"Gain glory, keep order, make alliances","difficulty":"15","thresholds":"9/18","hp":"4","stress":"3","attack":{"modifier":"+1","name":"Spear","range":"Very Close","damage":"2d8+4 phy"},"features":[{"name":"Reinforce - Action","text":"Mark a Stress to move into Melee range of an ally and make a standard attack against a target within Very Close range. On a success, deal 2d10+2 physical damage and the ally can clear a Stress."},{"name":"Vassal’s Loyalty - Reaction","text":"When the Soldier is within Very Close range of a knight or other noble who would take damage, you can mark a Stress to move into Melee range of them and take the damage instead."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-failed-experiment', 'adversary', 'Failed Experiment', NULL, 2, '{"description":"A magical necromantic experiment gone wrong, leaving them warped and ungainly.","motives":"Devour, hunt, track","difficulty":"13","thresholds":"12/23","hp":"3","stress":"3","attack":{"modifier":"+1","name":"Bite and Claw","range":"Melee","damage":"2d6+5 phy"},"features":[{"name":"Warped Fortitude - Passive","text":"The Experiment is resistant to physical damage."},{"name":"Overwhelm - Passive","text":"When a target the Experiment attacks has other adversaries within Very Close range, the Experiment deals double damage."},{"name":"Lurching Lunge - Action","text":"Mark a Stress to spotlight the Experiment as an additional GM move instead of spending Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-giant-beastmaster', 'adversary', 'Giant Beastmaster', NULL, 2, '{"description":"A leather-clad warrior bearing a whip and massive bow.","motives":"Command, make a living, maneuver, pin down, protect companion animals","difficulty":"16","thresholds":"12/24","hp":"6","stress":"5","attack":{"modifier":"+2","name":"Longbow","range":"Far","damage":"2d8+4 phy"},"features":[{"name":"Two as One - Passive","text":"When the Beastmaster is spotlighted, you can also spotlight a Tier 1 animal adversary currently under their control."},{"name":"Pinning Strike - Action","text":"Make a standard attack against a target. On a success, you can mark a Stress to pin them to a nearby surface. The pinned target is Restrained until they break free with a successful Finesse or Strength Roll."},{"name":"Deadly Companion - Action","text":"Twice per scene, summon a Bear, Dire Wolf, or similar Tier 1 animal adversary under the Beastmaster’s control. The adversary appears at Close range and is immediately spotlighted."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-giant-brawler', 'adversary', 'Giant Brawler', NULL, 2, '{"description":"An especially muscular giant wielding a warhammer larger than a human.","motives":"Make a living, overwhelm, slam, topple","difficulty":"15","thresholds":"12/28","hp":"7","stress":"4","attack":{"modifier":"+2","name":"Warhammer","range":"Very Close","damage":"2d12+3 phy"},"features":[{"name":"Battering Ram - Action","text":"Mark a Stress to have the Brawler charge at an inanimate object within Close range they could feasibly smash (such as a wall, cart, or market stand) and destroy it. All targets within Very Close range of the object must succeed on an Agility Reaction Roll or take 2d4+3 physical damage from the shrapnel."},{"name":"Bloody Reprisal - Reaction","text":"When the Brawler marks 2 or more HP from an attack within Very Close range, you can make a standard attack against the attacker. On a success, the Brawler deals 2d6+15 physical damage instead of their standard damage."},{"name":"Momentum - Reaction","text":"When the Brawler makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-giant-eagle', 'adversary', 'Giant Eagle', NULL, 2, '{"description":"A giant bird of prey with blood-stained talons.","motives":"Hunt prey, stay mobile, strike decisively","difficulty":"14","thresholds":"8/19","hp":"4","stress":"4","attack":{"modifier":"+1","name":"Claws and Beak","range":"Very Close","damage":"2d6+3 phy"},"features":[{"name":"Flight - Passive","text":"While flying, the Eagle gains a +3 bonus to their Difficulty."},{"name":"Deadly Dive - Action","text":"Mark a Stress to attack a target within Far range. On a success, deal 2d10+2 physical damage and knock the target over, making them Vulnerable until they next act."},{"name":"Take Off - Action","text":"Make an attack against a target within Very Close range. On a success, deal 2d4+3 physical damage and the target must succeed on an Agility Reaction Roll or become temporarily Restrained within the Eagle’s massive talons. If the target is Restrained, the Eagle immediately lifts them to the air to Very Far range above the battlefield while holding them."},{"name":"Deadly Drop - Action","text":"While flying, the Eagle can drop a Restrained target they are holding. When dropped, the target is no longer Restrained but starts falling. If their fall isn’t prevented during the PCs’ next action, the target takes 2d20 physical damage when they land."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-giant-recruit', 'adversary', 'Giant Recruit', NULL, 2, '{"description":"A giant fighter undergoing borrowed armor.","motives":"Batter, make a living, overwhelm, terrify","difficulty":"13","thresholds":"None","hp":"1","stress":"2","attack":{"modifier":"+1","name":"Warhammer","range":"Very Close","damage":"5 phy"},"features":[{"name":"Minion (7) - Passive","text":"The Recruit is defeated when they take any damage. For every 7 damage a PC deals to the Recruit, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Giant Recruits within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 5 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-gorgon', 'adversary', 'Gorgon', NULL, 2, '{"description":"A snake-headed, scaled humanoid with a gilded bow, enraged that their peace has been disturbed.","motives":"Corner, hit-and-run, petrify, seek vengeance","difficulty":"15","thresholds":"13/25","hp":"9","stress":"3","attack":{"modifier":"+4","name":"Sinew Shortbow","range":"Far","damage":"2d20+3 mag"},"features":[{"name":"Relentless (2) - Passive","text":"The Gorgon can be spotlighted up to two times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Suneater Arrows - Passive","text":"When the Gorgon makes a successful standard attack, the target Glows until the end of the scene and can’t become Hidden. Attack rolls made against a Glowing target have advantage."},{"name":"Crown of Serpents - Action","text":"Make an attack roll against a target within Melee range using the Gorgon’s protective snakes. On a success, mark Stress to deal 2d10+4 physical damage and the target must mark a Stress."},{"name":"Petrifying Gaze - Reaction","text":"When the Gorgon takes damage from an attack within Close range, you can spend a Fear to force the attacker to make an Instinct Reaction Roll. On a failure, they begin to turn to stone, marking a HP and starting a Petrification Countdown (4). This countdown ticks down when the Gorgon is attacked. When it triggers, the target must make a death move. If the Gorgon is defeated, all petrification countdowns end."},{"name":"Death Glare - Reaction","text":"When the Gorgon makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-juvenile-flickerfly', 'adversary', 'Juvenile Flickerfly', NULL, 2, '{"description":"A horse-sized insect with iridescent scales and crystalline wings moving faster than the eye can see.","motives":"Collect shiny things, hunt, swoop","difficulty":"14","thresholds":"13/26","hp":"10","stress":"5","attack":{"modifier":"+3","name":"Wing Slash","range":"Very Close","damage":"2d10+4 phy"},"features":[{"name":"Relentless (3) - Passive","text":"The Flickerfly can be spotlighted up to three times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Peerless Accuracy - Passive","text":"Before the Flickerfly makes an attack, roll a d6. On a result of 4 or higher, the target’s Evasion is halved against this attack."},{"name":"Mind Dance - Action","text":"Mark a Stress to create a magically dazzling display that grapples the minds of nearby foes. All targets within Close range must make an Instinct Reaction Roll. For each target who failed, you gain a Fear and the Flickerfly learns one of the target’s fears."},{"name":"Hallucinatory Breath - Reaction: Countdown (Loop 1d6)","text":"When the Flickerfly takes damage for the first time, activate the countdown. When it triggers, the Flickerfly breathes hallucinatory gas on all targets in front of them up to Far range. Targets must succeed on an Instinct Reaction Roll or be tormented by fearful hallucinations. Targets whose fears are known to the Flickerfly have disadvantage on this roll. Targets who fail must mark a Stress and lose a Hope."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-knight-of-the-realm', 'adversary', 'Knight of the Realm', NULL, 2, '{"description":"A decorated soldier with heavy armor and a powerful steed.","motives":"Run down, seek glory, show dominance","difficulty":"15","thresholds":"13/26","hp":"6","stress":"4","attack":{"modifier":"+4","name":"Longsword","range":"Melee","damage":"2d10+4 phy"},"features":[{"name":"Chevalier - Passive","text":"While the Knight is on a mount, they gain a +2 bonus to their Difficulty. When they take Severe damage, they’re knocked from their mount and lose this benefit until they’re next spotlighted."},{"name":"Heavily Armored - Passive","text":"When the Knight takes physical damage, reduce it by 3."},{"name":"Cavalry Charge - Action","text":"If the Knight is mounted, move up to Far range and make a standard attack against a target. On a success, deal 2d8+4 physical damage and the target must mark a Stress."},{"name":"For the Realm! - Action","text":"Mark a Stress to spotlight 1d4+1 allies. Attacks they make while spotlighted in this way deal half damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-masked-thief', 'adversary', 'Masked Thief', NULL, 2, '{"description":"A cunning thief with acrobatic skill and a flair for the dramatic.","motives":"Evade, hide, pilfer, profit","difficulty":"14","thresholds":"8/17","hp":"4","stress":"5","attack":{"modifier":"+3","name":"Backsword","range":"Melee","damage":"2d8+3 phy"},"features":[{"name":"Quick Hands - Action","text":"Make an attack against a target within Melee range. On a success, deal 1d8+2 physical damage and the Thief steals one item or consumable from the target’s inventory."},{"name":"Escape Plan - Action","text":"Mark a Stress to reveal a snare trap set anywhere on the battlefield by the Thief. All targets within Very Close range of the trap must succeed on an Agility Reaction Roll (13) or be pulled off their feet and suspended upside down. The target is Restrained and Vulnerable until they break free, ending both conditions, with a successful Finesse or Strength Roll (13)."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-merchant-baron', 'adversary', 'Merchant Baron', NULL, 2, '{"description":"An accomplished merchant with a large operation under their command.","motives":"Abusive power, gather resources, mobilize minions","difficulty":"15","thresholds":"9/19","hp":"5","stress":"3","attack":{"modifier":"+2","name":"Rapier","range":"Melee","damage":"1d6+2 phy"},"features":[{"name":"Everyone Has a Price - Action","text":"Spend a Fear to offer a target a dangerous bargain for something they want or need. If used on a PC, they must make a Presence Reaction Roll (17). On a failure, they must mark 2 Stress or take the deal."},{"name":"The Best Muscle Money Can Buy - Action","text":"Once per scene, mark a Stress to summon 1d4+1 Tier 1 adversaries, who appear at Far range, to enforce the Baron’s will."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-minotaur-wrecker', 'adversary', 'Minotaur Wrecker', NULL, 2, '{"description":"A massive bull-headed hybrid with a quick temper.","motives":"Consume, gore, navigate, overpower, pursue","difficulty":"16","thresholds":"14/27","hp":"7","stress":"5","attack":{"modifier":"+2","name":"Battleaxe","range":"Very Close","damage":"2d8+5 phy"},"features":[{"name":"Ramp Up - Passive","text":"You must spend a Fear to spotlight the Minotaur. While spotlighted, they can make their standard attack against all targets within range."},{"name":"Charging Bull - Action","text":"Mark a Stress to charge through a group within Close range and make an attack against all targets in the Minotaur’s path. Targets the Minotaur succeeds against take 2d6+8 physical damage and are knocked back to Very Far range. If a target is knocked into a solid object or another creature, they take an extra 1d6 damage (combine their damage)."},{"name":"Gore - Action","text":"Make an attack against a target within Very Close range, moving the Minotaur into Melee range of them. On a success, deal 2d8 direct physical damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-mortal-hunter', 'adversary', 'Mortal Hunter', NULL, 2, '{"description":"An undead figure wearing a heavy leather coat, with searching eyes and a cruelly cut demeanor.","motives":"Devour, hunt, track","difficulty":"16","thresholds":"15/27","hp":"6","stress":"4","attack":{"modifier":"+5","name":"Tear at Flesh","range":"Very Close","damage":"2d12+1 phy"},"features":[{"name":"Terrifying - Passive","text":"When the Hunter makes a successful attack, all PCs within Far range lose a Hope and you gain a Fear."},{"name":"Deathlock - Action","text":"Spend a Fear to curse a target within Very Close range with a necrotic Deathlock until the end of the scene. Attacks made by the Hunter against a Deathlocked target deal direct damage. The Hunter can only maintain one Deathlock at a time."},{"name":"Inevitable Death - Action","text":"Mark a Stress to spotlight 1d4 allies. Attacks they make while spotlighted in this way deal half damage."},{"name":"Rampage - Reaction","text":"Countdown (Loop 1d6). When the Hunter is in the spotlight for the first time, activate the countdown. When it triggers, move the Hunter in a straight line to a point within Far range and make an attack against all targets in their path. Targets the Hunter succeeds against take 2d8+2 physical damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-royal-advisor', 'adversary', 'Royal Advisor', NULL, 2, '{"description":"A high-ranking courtier with the ear of the local nobility.","motives":"Curry favor, manufacture evidence, scheme","difficulty":"14","thresholds":"8/15","hp":"3","stress":"3","attack":{"modifier":"-3","name":"Wand","range":"Far","damage":"1d4+3 phy"},"features":[{"name":"Devastating Retort - Passive","text":"A PC who rolls less than 17 on an action roll targeting the Advisor must mark a Stress."},{"name":"Bend Ears - Action","text":"Mark a Stress to influence an NPC within Melee range with whispered words. That target’s opinion on one matter shifts toward the Advisor’s preference unless it is in direct opposition to the target’s motives."},{"name":"Scapegoat - Action","text":"Spend a Fear to convince a crowd or notable individual that one person or group is responsible for some problem facing the target. The target becomes hostile to the scapegoat until convinced of their innocence with a successful Presence Roll (17)."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-secret-keeper', 'adversary', 'Secret-Keeper', NULL, 2, '{"description":"A clandestine leader with a direct channel to the Fallen Gods.","motives":"Amass great power, plot, take command","difficulty":"16","thresholds":"13/26","hp":"7","stress":"4","attack":{"modifier":"+3","name":"Sigil-Laden Staff","range":"Far","damage":"2d12 mag"},"features":[{"name":"Seize Your Moment - Action","text":"Spend 2 Fear to spotlight 1d4 allies. Attacks they make while spotlighted in this way deal half damage."},{"name":"Our Master’s Will - Reaction","text":"When you spotlight an ally within Far range, mark a Stress to gain a Fear."},{"name":"Summoning Ritual - Reaction","text":"Countdown (6). When the Secret-Keeper is in the spotlight for the first time, activate the countdown. When they mark HP, tick down this countdown by the number of HP marked. When it triggers, summon a Minor Demon who appears at Close range."},{"name":"Fallen Hounds - Reaction","text":"Once per scene, when the Secret-Keeper marks 2 or more HP, you can mark a Stress to summon a Demonic Hound Pack, which appears at Close range and is immediately spotlighted."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-shark', 'adversary', 'Shark', NULL, 2, '{"description":"A large aquatic predator, always on the move.","motives":"Find the blood, isolate prey, target the weak","difficulty":"14","thresholds":"14/28","hp":"7","stress":"3","attack":{"modifier":"+2","name":"Toothy Maw","range":"Very Close","damage":"2d12+1 phy"},"features":[{"name":"Terrifying - Passive","text":"When the Shark makes a successful attack, all PCs within Far range lose a Hope and you gain a Fear."},{"name":"Rending Bite - Passive","text":"When the Shark makes a successful attack, the target must mark an Armor Slot without receiving its benefits (they can still use armor to reduce the damage). If they can’t mark an Armor Slot, they must mark an additional HP."},{"name":"Blood in the Water - Reaction","text":"When a creature within Close range of the Shark marks HP from another creature’s attack, you can mark a Stress to immediately spotlight the Shark, moving them into Melee range of the target and making a standard attack."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-siren', 'adversary', 'Siren', NULL, 2, '{"description":"A half fish person with shimmering scales and an irresistible voice.","motives":"Consume, lure prey, subdue with song","difficulty":"14","thresholds":"9/18","hp":"5","stress":"3","attack":{"modifier":"+2","name":"Distended Jaw Bite","range":"Melee","damage":"2d6+3 phy"},"features":[{"name":"Captive Audience - Passive","text":"If the Siren makes a standard attack against a target Entranced by their song, the attack deals 2d10+1 damage instead of their standard damage."},{"name":"Enchanting Song - Action","text":"Spend a Fear to sing a song that affects all targets within Close range. Targets must succeed on an Instinct Reaction Roll or become Entranced until they mark 2 Stress. Other Sirens within Close range of the target can mark a Stress to each add a +1 bonus to the Difficulty of the reaction roll. While Entranced, a target can’t act and is Vulnerable."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-spectral-archer', 'adversary', 'Spectral Archer', NULL, 2, '{"description":"A ghostly fighter with an ethereal bow, unable to move on while their charge is vulnerable.","motives":"Move through solid objects, stay out of the fray, rehash old battles","difficulty":"13","thresholds":"6/14","hp":"3","stress":"3","attack":{"modifier":"+3","name":"Longbow","range":"Far","damage":"2d10+2 phy"},"features":[{"name":"Ghost - Passive","text":"The Archer has resistance to physical damage. Mark a Stress to move up to Close range through solid objects."},{"name":"Pick Your Target - Action","text":"Spend a Fear to make an attack against a target within Very Close range of at least two other PCs. On a success, the target takes 2d8+12 physical damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-spectral-captain', 'adversary', 'Spectral Captain', NULL, 2, '{"description":"A ghostly commander leading their troops beyond death.","motives":"Move through solid objects, rally troops, rehash old battles","difficulty":"16","thresholds":"13/26","hp":"6","stress":"4","attack":{"modifier":"+3","name":"Longbow","range":"Far","damage":"2d10+3 phy"},"features":[{"name":"Ghost - Passive","text":"The Captain has resistance to physical damage. Mark a Stress to move up to Close range through solid objects."},{"name":"Unending Battle - Action","text":"Spend 2 Fear to return up to 1d4+1 defeated Spectral allies to the battle at the points where they first appeared (with no marked HP or Stress)."},{"name":"Hold Fast - Reaction","text":"When the Captain’s Spectral allies are forced to make a reaction roll, you can mark a Stress to give those allies a +2 bonus to the roll."},{"name":"Momentum - Reaction","text":"When the Captain makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-spectral-guardian', 'adversary', 'Spectral Guardian', NULL, 2, '{"description":"A ghostly fighter with spears and swords, anchored by duty.","motives":"Move through solid objects, protect treasure, rehash old battles","difficulty":"15","thresholds":"7/15","hp":"4","stress":"3","attack":{"modifier":"+1","name":"Spear","range":"Very Close","damage":"2d8+1 phy"},"features":[{"name":"Ghost - Passive","text":"The Guardian has resistance to physical damage. Mark a Stress to move up to Close range through solid objects."},{"name":"Grave Blade - Action","text":"Spend a Fear to make an attack against a target within Very Close range. On a success, deal 2d10+6 physical damage and the target must mark a Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-spy', 'adversary', 'Spy', NULL, 2, '{"description":"A skilled espionage agent with a knack for being in the right place to overhear secrets.","motives":"Cut and run, disguise appearance, eavesdrop","difficulty":"15","thresholds":"8/17","hp":"4","stress":"3","attack":{"modifier":"-2","name":"Dagger","range":"Melee","damage":"2d6+3 phy"},"features":[{"name":"Gathering Secrets - Action","text":"Spend a Fear to describe how the Spy knows a secret about a PC in the scene."},{"name":"Fly on the Wall - Reaction","text":"When a PC or group is discussing something sensitive, you can mark a Stress to reveal that the Spy is present in the scene, observing them. If the Spy escapes the scene to report their findings, you gain 1d4 Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-stonewraith', 'adversary', 'Stonewraith', NULL, 2, '{"description":"A prowling hunter, like a slinking mountain lion, with a slate-gray stone body.","motives":"Defend territory, isolate prey, stalk","difficulty":"13","thresholds":"11/22","hp":"6","stress":"3","attack":{"modifier":"+3","name":"Bite and Claws","range":"Melee","damage":"2d8+6 phy"},"features":[{"name":"Stonestrider - Passive","text":"The Stonewraith can move through stone and earth as easily as air. While within stone or earth, they are Hidden and immune to all damage."},{"name":"Rocky Ambush - Action","text":"While Hidden, mark a Stress to leap into Melee range with a target within Very Close range. The target must succeed on an Agility or Instinct Reaction Roll (15) or take 2d8 physical damage and become temporarily Restrained."},{"name":"Avalanche Roar - Action","text":"Spend a Fear to roar while within a cave and cause a cave-in. All targets within Close range must succeed on an Agility Reaction Roll (14) or take 2d10 physical damage. The rubble can be cleared with a Progress Countdown (8)."},{"name":"Momentum - Reaction","text":"When the Stonewraith makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-war-wizard', 'adversary', 'War Wizard', NULL, 2, '{"description":"A battle-hardened mage trained in destructive magic.","motives":"Develop new spells, seek power, conquer","difficulty":"16","thresholds":"11/23","hp":"5","stress":"6","attack":{"modifier":"+4","name":"Staff","range":"Far","damage":"2d10+4 mag"},"features":[{"name":"Battle Teleport - Passive","text":"Before or after making a standard attack, you can mark a Stress to teleport to a location within Far range."},{"name":"Refresh Warding Sphere - Action","text":"Mark a Stress to refresh the Wizard’s “Warding Sphere” reaction."},{"name":"Eruption - Action","text":"Spend a Fear and choose a point within Far range. A Very Close area around that point erupts into impassable terrain. All targets within that area must make an Agility Reaction Roll (14). Targets who fail take 2d10 physical damage and are thrown out of the area. Targets who succeed take half damage and aren’t moved."},{"name":"Arcane Artillery - Action","text":"Spend a Fear to unleash a precise hail of magical blasts. All targets in the scene must make an Agility Reaction Roll. Targets who fail take 2d12 magic damage. Targets who succeed take half damage."},{"name":"Warding Sphere - Reaction","text":"When the Wizard takes damage from an attack within Close range, deal 2d6 magic damage to the attacker. This reaction can’t be used again until the Wizard refreshes it with their “Refresh Warding Sphere” action."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-adult-flickerfly', 'adversary', 'Adult Flickerfly', NULL, 3, '{"description":"A winged insect the size of a large house with iridescent scales and wings that move too fast to track.","motives":"Collect shiny things, hunt, nest, swoop","difficulty":"17","thresholds":"20/35","hp":"12","stress":"6","attack":{"modifier":"+3","name":"Wing Slash","range":"Very Close","damage":"3d20 phy"},"features":[{"name":"Relentless (4) - Passive","text":"The Flickerfly can be spotlighted up to four times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Never Misses - Passive","text":"When the Flickerfly makes an attack, the target’s Evasion is halved against the attack."},{"name":"Deadly Flight - Passive","text":"While flying, the Flickerfly can move up to Far range instead of Close range before taking an action."},{"name":"Whirlwind - Action","text":"Spend a Fear to whirl, making an attack against all targets within Very Close range. Targets the Flickerfly succeeds against take 3d8 direct physical damage."},{"name":"Mind Dance - Action","text":"Mark a Stress to create a magically dazzling display that grapples the minds of nearby foes. All targets within Close range must make an Instinct Reaction Roll. For each target who failed, you gain a Fear and the Flickerfly learns one of the target’s fears."},{"name":"Hallucinatory Breath - Action: Countdown (Loop 1d6)","text":"When the Flickerfly takes damage for the first time, activate the countdown. When it triggers, the Flickerfly breathes hallucinatory gas on all targets in front of them up to Far range. Targets must make an Instinct Reaction Roll or become overwhelmed by fearful hallucinations. Targets whose fears are known to the Flickerfly have disadvantage on this roll. Targets who fail lose 2 Hope and take 3d8+3 direct magic damage."},{"name":"Uncanny Reflexes - Reaction","text":"When the Flickerfly takes damage from an attack within Close range, you can mark a Stress to take half damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-demon-of-avarice', 'adversary', 'Demon of Avarice', NULL, 3, '{"description":"A regal cloaked monstrosity with circular horns adorned with treasure.","motives":"Consume, fuel greed, sow dissent","difficulty":"17","thresholds":"15/29","hp":"6","stress":"5","attack":{"modifier":"+2","name":"Hungry Maw","range":"Melee","damage":"3d6+5 mag"},"features":[{"name":"Money Talks - Passive","text":"Attacks against the Demon are made with disadvantage unless the attacker spends a handful of gold. This Demon starts with a number of handfuls equal to the number of PCs. When a target marks HP from the Demon’s standard attack, they can spend a handful of gold instead of marking HP (1 handful per HP). Add a handful of gold to the Demon for each handful of gold spent by PCs on this feature."},{"name":"Numbers Must Go Up - Passive","text":"Add a bonus to the Demon’s attack rolls equal to the number of handfuls of gold they have."},{"name":"Money is Time - Action","text":"Spend 3 handfuls of gold (or a Fear) to spotlight 1d4+1 allies."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-demon-of-despair', 'adversary', 'Demon of Despair', NULL, 3, '{"description":"A cloaked one-creature with long limbs, seeping shadows.","motives":"Make fear contagious, stick to the shadows, undermine resolve","difficulty":"17","thresholds":"18/35","hp":"6","stress":"5","attack":{"modifier":"+3","name":"Miasma Bolt","range":"Far","damage":"3d6+4 mag"},"features":[{"name":"Depths of Despair - Passive","text":"The Demon deals double damage to PCs with 0 Hope."},{"name":"Your Struggle Is Pointless - Action","text":"Spend a Fear to weigh down the spirits of all PCs within Far range. All targets affected replace their Hope Die with a d8 until they roll a success with Hope or their next rest."},{"name":"Your Friends Will Fail You - Reaction","text":"When a PC fails with Fear, you can mark a Stress to cause all other PCs within Close range to lose a Hope."},{"name":"Momentum - Reaction","text":"When the Demon makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-demon-of-hubris', 'adversary', 'Demon of Hubris', NULL, 3, '{"description":"A perfectly beautiful and infinitely cruel demon with a gleaming spear and elegant robes.","motives":"Condescend, declare premature victory, prove superiority","difficulty":"19","thresholds":"20/36","hp":"7","stress":"5","attack":{"modifier":"+4","name":"Perfect Spear","range":"Very Close","damage":"3d10 phy"},"features":[{"name":"Terrifying - Passive","text":"When the Demon makes a successful attack, all PCs within Far range must lose a Hope and you gain a Fear."},{"name":"Double or Nothing - Passive","text":"When a PC within Far range fails a roll, they can choose to reroll their Fear Die and take the new result. If they still fail, they mark 2 Stress and the Demon clears a Stress."},{"name":"Unparalleled Skill - Action","text":"Mark a Stress to deal the Demon’s standard attack damage to a target within Close range."},{"name":"The Root of Villainy - Action","text":"Spend a Fear to spotlight two other Demons within Far range."},{"name":"You Pale in Comparison - Reaction","text":"When a PC fails a roll within Close range of the Demon, they must mark a Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-demon-of-jealousy', 'adversary', 'Demon of Jealousy', NULL, 3, '{"description":"A fickle creature of spindly limbs and insatiable desires.","motives":"Join in on others’ success, take what belongs to others, hold grudges","difficulty":"17","thresholds":"17/30","hp":"6","stress":"6","attack":{"modifier":"+4","name":"Psychic Assault","range":"Far","damage":"3d8+3 mag"},"features":[{"name":"Unprotected Mind - Passive","text":"The Demon’s standard attack deals direct damage."},{"name":"My Turn - Reaction","text":"When the Demon marks HP from an attack, spend a number of Fear equal to the HP marked by the Demon to cause the attacker to mark the same number of HP."},{"name":"Rivalry - Reaction","text":"When a creature within Close range takes damage from a different adversary, you can mark a Stress to add a d4 to the damage roll."},{"name":"What’s Yours Is Mine - Reaction","text":"When a PC takes severe damage within Very Close range of the Demon, you can spend a Fear to cause the target to make a Finesse Reaction Roll. On a failure, the Demon seizes one item or consumable of their choice from the target’s inventory."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-demon-of-wrath', 'adversary', 'Demon of Wrath', NULL, 3, '{"description":"A hulking demon with boulder-sized fists, driven by endless rage.","motives":"Fuel anger, impress rivals, wreak havoc","difficulty":"17","thresholds":"22/40","hp":"7","stress":"5","attack":{"modifier":"+3","name":"Fists","range":"Very Close","damage":"3d8+1 mag"},"features":[{"name":"Anger Unrelenting - Passive","text":"The Demon’s attacks deal direct damage."},{"name":"Battle Lust - Action","text":"Spend a Fear to boil the blood of all PCs within Far range. They use a d20 as their Fear Die until the end of the scene."},{"name":"Retaliation - Reaction","text":"When the Demon takes damage from an attack within Close range, you can mark a Stress to make a standard attack against the attacker."},{"name":"Blood and Souls - Reaction: Countdown (Loop 6)","text":"Activate the first time an attack is made within sight of the Demon. It ticks down when a PC takes a violent action. When it triggers, summon 1d4 Minor Demons, who appear at Close range."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-dire-bat', 'adversary', 'Dire Bat', NULL, 3, '{"description":"A winged pet endlessly loyal to their vampire owner.","motives":"Dive-bomb, hide, protect leader","difficulty":"14","thresholds":"16/30","hp":"5","stress":"3","attack":{"modifier":"+2","name":"Claws and Teeth","range":"Melee","damage":"2d6+7 phy"},"features":[{"name":"Flying - Passive","text":"While flying, the Bat gains a +3 bonus to their Difficulty."},{"name":"Screech - Action","text":"Mark a Stress to send a high-pitch screech out toward all targets in front of the Bat within Far range. Those targets must mark 1d4 Stress."},{"name":"Guardian - Reaction","text":"When an allied Vampire marks HP, you can mark a Stress to fly into Melee range of the attacker and make an attack with advantage against them. On a success, deal 2d6+2 physical damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-dryad', 'adversary', 'Dryad', NULL, 3, '{"description":"A nature spirit in the form of a humanoid tree.","motives":"Camouflage, drive out, preserve the forest","difficulty":"16","thresholds":"24/38","hp":"8","stress":"5","attack":{"modifier":"+4","name":"Deadfall Shortbow","range":"Far","damage":"3d10+1 phy"},"features":[{"name":"Bramble Patch - Action","text":"Mark a Stress to target a point within Far range. Create a patch of thorns that covers an area within Close range of that point. All targets within that area take 2d6+2 physical damage when they act. A target must succeed on a Finesse Roll or take more than 20 damage to the Dryad with an attack to leave the area."},{"name":"Group Saplings - Action","text":"Spend a Fear to grow three Treant Sapling Minions, who appear at Close range and immediately take the spotlight."},{"name":"We Are All One - Reaction","text":"When an ally dies within Close range, you can spend a Fear to clear 2 HP and 2 Stress as the fallen ally’s life force is returned to the forest."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-elemental-spark', 'adversary', 'Elemental Spark', NULL, 3, '{"description":"A blazing mote of elemental fire.","motives":"Blast, consume, gain mass","difficulty":"15","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"+0","name":"Burst of Fire","range":"Close","damage":"5 mag"},"features":[{"name":"Minion (9) - Passive","text":"The Elemental is defeated when they take any damage. For every 9 damage a PC deals to the Elemental, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Elemental Sparks within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 5 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-greater-earth-elemental', 'adversary', 'Greater Earth Elemental', NULL, 3, '{"description":"A living landslide of boulders and dust, as large as a house.","motives":"Avalanche, knock over, pummel","difficulty":"17","thresholds":"22/40","hp":"10","stress":"4","attack":{"modifier":"+7","name":"Boulder Fist","range":"Very Close","damage":"3d10+1 phy"},"features":[{"name":"Slow - Passive","text":"When you spotlight the Elemental and they don’t have a token on their stat block, they can’t act yet. Place a token on their stat block and describe what they’re preparing to do. When you spotlight the Elemental and they have a token on their stat block, clear the token and they can act."},{"name":"Crushing Blows - Passive","text":"When the Elemental makes a successful attack, the target must mark an Armor Slot without receiving its benefits (they can still use armor to reduce the damage). If they can’t mark an Armor Slot, they must mark an additional HP."},{"name":"Immovable Object - Passive","text":"An attack that would move the Elemental moves them two fewer ranges (for example, Far becomes Very Close). When the Elemental takes physical damage, reduce it by 7."},{"name":"Rockslide - Action","text":"Mark a Stress to create a rockslide that buries all the land in front of Elemental within Close range with rockfall. All targets in this area must make an Agility Reaction Roll (19). Targets who fail take 2d12+5 physical damage and become Vulnerable until their next roll with Hope. Targets who succeed take half damage."},{"name":"Momentum - Reaction","text":"When the Elemental makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-greater-water-elemental', 'adversary', 'Greater Water Elemental', NULL, 3, '{"description":"A huge living wave that crashes down upon enemies.","motives":"Deluge, disperse, drown","difficulty":"17","thresholds":"17/34","hp":"5","stress":"5","attack":{"modifier":"+3","name":"Crashing Wave","range":"Very Close","damage":"3d4+1 mag"},"features":[{"name":"Water Jet - Action","text":"Mark a Stress to attack a target within Very Close range. On a success, deal 2d4+7 physical damage and the target’s next action has disadvantage. On a failure, the target must mark a Stress."},{"name":"Drowning Embrace - Action","text":"Spend a Fear to make an attack against all targets within Very Close range. Targets the Elemental succeeds against become Restrained and Vulnerable as they begin drowning. A target can break free, ending both conditions, with a successful Strength or Instinct Roll."},{"name":"High Tide - Reaction","text":"When the Elemental makes a successful standard attack, you can mark a Stress to knock the target back to Close range."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-huge-green-ooze', 'adversary', 'Huge Green Ooze', NULL, 3, '{"description":"A translucent green mound of acid taller than most humans.","motives":"Camouflage, creep up, envelop, multiply","difficulty":"15","thresholds":"15/30","hp":"7","stress":"4","attack":{"modifier":"+3","name":"Ooze Appendage","range":"Melee","damage":"3d8+1 mag"},"features":[{"name":"Slow - Passive","text":"When you spotlight the Ooze and they don’t have a token on their stat block, they can’t act yet. Place a token on their stat block and describe what they’re preparing to do. When you spotlight the Ooze and they have a token on their stat block, clear the token and they can act."},{"name":"Acidic Form - Passive","text":"When the Ooze makes a successful attack, the target must mark an Armor Slot without receiving its benefits (they can still use armor to reduce the damage). If they can’t mark an Armor Slot, they must mark an additional HP."},{"name":"Envelop - Action","text":"Make an attack against a target within Melee range. On a success, the Ooze Envelops them and the target must mark 2 Stress. While Enveloped, the target must mark an additional Stress every time they make an action roll. When the Ooze takes Severe damage, all Enveloped targets are freed and the condition is cleared."},{"name":"Split - Reaction","text":"When the Ooze has 4 or more HP marked, you can spend a Fear to split them into two Green Oozes (with no marked HP or Stress). Immediately spotlight both of them."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-hydra', 'adversary', 'Hydra', NULL, 3, '{"description":"A quadrupedal scaled beast with multiple long-necked heads, each filled with menacing fangs.","motives":"Devour, regenerate, terrify","difficulty":"18","thresholds":"19/35","hp":"10","stress":"5","attack":{"modifier":"+3","name":"Bite","range":"Close","damage":"2d12+2 phy"},"features":[{"name":"Many-Headed Menace - Passive","text":"The Hydra begins with three heads and can have up to five. When the Hydra takes Major or greater damage, they lose a head."},{"name":"Relentless (X) - Passive","text":"The Hydra can be spotlighted X times per GM turn, where X is the Hydra’s number of heads. Spend Fear as usual to spotlight them."},{"name":"Regeneration - Action","text":"If the Hydra has any marked HP, spend a Fear to clear a HP and grow two heads."},{"name":"Terrifying Chorus - Action","text":"All PCs within Far range lose 2 Hope."},{"name":"Magical Weakness - Reaction","text":"When the Hydra takes magic damage, they become Dazed until the next roll with Fear. While Dazed, they can’t use their Regeneration action but are immune to magic damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-monarch', 'adversary', 'Monarch', NULL, 3, '{"description":"The sovereign ruler of a nation, unearthed in the privilege of tradition and wielding unmatched power in their domain.","motives":"Control vassals, destroy rivals, forge a legacy","difficulty":"16","thresholds":"16/32","hp":"6","stress":"5","attack":{"modifier":"+0","name":"Warhammer","range":"Melee","damage":"3d6+3 phy"},"features":[{"name":"Execute Them! - Action","text":"Spend a Fear per PC in the party to have the group condemned for crimes real or imagined. A PC who succeeds on a Presence Roll can demand trial by combat or another special form of trial."},{"name":"Crossguard - Action","text":"Once per scene, mark a Stress to summon Tier X Minions, who appear at Close range to enforce the Monarch’s will."},{"name":"Census Bell - Reaction: Long-Term Countdown (8)","text":"Spend a Fear to activate after the Monarch’s desire for war is first revealed. When it triggers, the Monarch has a reason to rally the nation to war and the support to act on that reason. You gain 1d4 Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-stag-knight', 'adversary', 'Stag Knight', NULL, 3, '{"description":"A knight with huge, majestic antlers wearing armor made of dangerous thorns.","motives":"Isolate, maneuver, protect the forest, weed the unwelcome","difficulty":"17","thresholds":"19/36","hp":"7","stress":"5","attack":{"modifier":"+3","name":"Bramble Sword","range":"Melee","damage":"3d8+3 phy"},"features":[{"name":"From Above - Passive","text":"When the Knight succeeds on a standard attack from above a target, they deal 3d12+3 physical damage instead of their standard damage."},{"name":"Blade of the Forest - Action","text":"Spend a Fear to make an attack against all targets within Very Close range. Targets the Knight succeeds against take physical damage equal to 3d4 + the target''s Major threshold."},{"name":"Thorny Armor - Reaction","text":"When the Knight takes damage from an attack within Melee range, you can mark a Stress to deal 1d10+5 physical damage to the attacker."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-oak-treant', 'adversary', 'Oak Treant', NULL, 3, '{"description":"A sturdy animated old-growth tree.","motives":"Hide in plain sight, preserve the forest, root down, swing branches","difficulty":"17","thresholds":"22/40","hp":"7","stress":"4","attack":{"modifier":"+2","name":"Branch","range":"Very Close","damage":"3d8+2 phy"},"features":[{"name":"Just a Tree - Passive","text":"Before they make their first attack in a fight or after they become Hidden, the Treant is indistinguishable from other trees until they next act or a PC succeeds on an Instinct Roll to identify them."},{"name":"Seed Barrage - Action","text":"Mark a Stress and make an attack against up to three targets within Close range, pummeling them with giant acorns. Targets the Treant succeeds against take 2d10+5 physical damage."},{"name":"Take Root - Action","text":"Mark a Stress to Root the Treant in place. The Treant is Restrained while Rooted, and can end this effect instead of moving while they are spotlighted. While Rooted, the Treant has resistance to physical damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-treant-sapling', 'adversary', 'Treant Sapling', NULL, 3, '{"description":"A small, sentient tree sapling.","motives":"Blend in, preserve the forest, pummel, surround","difficulty":"14","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"+0","name":"Branches","range":"Melee","damage":"8 phy"},"features":[{"name":"Minion (6) - Passive","text":"The Sapling is defeated when they take any damage. For every 6 damage a PC deals to the Sapling, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Treant Saplings within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 8 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-head-vampire', 'adversary', 'Head Vampire', NULL, 3, '{"description":"A captivating undead dressed in aristocratic finery.","motives":"Create thralls, charm, command, fly, intimidate","difficulty":"17","thresholds":"22/42","hp":"6","stress":"6","attack":{"modifier":"+5","name":"Rapier","range":"Melee","damage":"2d20+4 phy"},"features":[{"name":"Terrifying - Passive","text":"When the Vampire makes a successful attack, all PCs within Far range lose a Hope and you gain a Fear."},{"name":"Look Into My Eyes - Passive","text":"A creature who moves into Melee range of the Vampire must make an Instinct Reaction Roll. On a failure, you gain 1d4 Fear."},{"name":"Feed on Followers - Action","text":"When the Vampire is within Melee range of an ally, they can cause the ally to mark a HP. The Vampire then clears a HP."},{"name":"The Hunt Is On - Action","text":"Spend 2 Fear to summon 1d4 Vampires, who appear at Far range and immediately take the spotlight."},{"name":"Lifesuck - Reaction","text":"When the Vampire is spotlighted, roll a d8. On a result of 6 or higher, all targets within Very Close range must mark a HP."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-vampire', 'adversary', 'Vampire', NULL, 3, '{"description":"An intelligent undead with blood-stained lips and a predator’s smile.","motives":"Bite, charm, deceive, feed, intimidate","difficulty":"16","thresholds":"18/35","hp":"5","stress":"4","attack":{"modifier":"+3","name":"Rapier","range":"Melee","damage":"3d8 phy"},"features":[{"name":"Draining Bite - Action","text":"Make an attack against a target within Melee range. On a success, deal 4d physical damage. A target who marks HP from this attack loses a Hope and must mark a Stress. The Vampire then clears a HP."},{"name":"Mistform - Reaction","text":"When the Vampire takes physical damage, you can spend a Fear to take half damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-vault-guardian-sentinel', 'adversary', 'Vault Guardian Sentinel', NULL, 3, '{"description":"A dust-covered golden construct with boxy limbs and a huge mace for a hand.","motives":"Destroy at any cost, expunge, protect","difficulty":"17","thresholds":"21/40","hp":"6","stress":"3","attack":{"modifier":"+3","name":"Charged Mace","range":"Very Close","damage":"2d12+1 phy"},"features":[{"name":"Kinetic Slam - Passive","text":"Targets who take damage from the Sentinel’s standard attack are knocked back to Very Close range."},{"name":"Box In - Action","text":"Mark a Stress to choose a target within Very Close range to focus on. That target has disadvantage on attack rolls when they’re within Very Close range of the Sentinel. The Sentinel can only focus on one target at a time."},{"name":"Mana Bolt - Action","text":"Spend a Fear to lob explosive magic at a point within Far range. All targets within Very Close range of that point must make an Agility Reaction Roll. Targets who fail take 8d20 magic damage and are knocked back to Close range. Targets who succeed take half damage and aren’t knocked back."},{"name":"Momentum - Reaction","text":"When the Sentinel makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-vault-guardian-turret', 'adversary', 'Vault Guardian Turret', NULL, 3, '{"description":"A massive hulking turret with reinforced armor and twelve piston-driven mechanical legs.","motives":"Concentrate fire, lock down, mark, protect","difficulty":"16","thresholds":"20/32","hp":"5","stress":"4","attack":{"modifier":"+3","name":"Magitech Cannon","range":"Far","damage":"3d10+3 mag"},"features":[{"name":"Slow Firing - Passive","text":"When you spotlight the Turret and they don’t have a token on their stat block, they can’t make a standard attack. Place a token on their stat block and describe what they’re preparing to do. When you spotlight the Turret and they have a token on their stat block, clear the token and they can attack."},{"name":"Mark Target - Action","text":"Spend a Fear to Mark a target within Far range until the Turret is destroyed or the Marked target becomes Hidden. While the target is Marked, their Evasion is halved."},{"name":"Concentrate Fire - Reaction","text":"When another adversary deals damage to a target within Far range of the Turret, you can mark a Stress to add the Turret’s standard attack damage to the damage roll."},{"name":"Detonation - Reaction","text":"When the Turret is destroyed, they explode. All targets within Close range must make an Agility Reaction Roll. Targets who fail take 3d20 physical damage. Targets who succeed take half damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-vault-guardian-gaoler', 'adversary', 'Vault Guardian Gaoler', NULL, 3, '{"description":"A boxy, dust-covered construct with thick metallic swinging doors on their torso.","motives":"Carry away, entrap, protect, pummel","difficulty":"16","thresholds":"19/33","hp":"5","stress":"3","attack":{"modifier":"+2","name":"Body Bash","range":"Very Close","damage":"3d6+2 phy"},"features":[{"name":"Blocking Shield - Passive","text":"Creatures within Melee range of the Gaoler have disadvantage on attack rolls against them. Creatures trapped inside the Gaoler are immune to this feature."},{"name":"Lock Up - Action","text":"Mark a Stress to make an attack against a target within Very Close range. On a success, the target is Restrained within the Gaoler until freed with a successful Strength Roll (18). While Restrained, the target can only attack the Gaoler."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-young-ice-dragon', 'adversary', 'Young Ice Dragon', NULL, 3, '{"description":"A glacier-blue dragon with four powerful limbs and frost-tinged wings.","motives":"Avalanche, defend lair, fly, freeze, defend what is mine, maul","difficulty":"18","thresholds":"21/41","hp":"10","stress":"6","attack":{"modifier":"+7","name":"Bite and Claws","range":"Close","damage":"4d10 phy"},"features":[{"name":"Relentless (3) - Passive","text":"The Dragon can be spotlighted up to three times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Rend and Crush - Passive","text":"If a target damaged by the Dragon doesn’t mark an Armor Slot to reduce the damage, they must mark a Stress."},{"name":"No Hope - Passive","text":"When a PC rolls with Fear while within Far range of the Dragon, they lose a Hope."},{"name":"Blizzard Breath - Action","text":"Spend 2 Fear to release an icy whirlwind in an area within Close range. All targets in this area must make an Agility Reaction Roll. Targets who fail take 4d6+5 magic damage and are Restrained by ice until they break free with a successful Strength Roll. Targets who succeed must mark 2 Stress or take half damage."},{"name":"Avalanche - Action","text":"Spend a Fear to have the Dragon unleash a huge downfall of snow and ice, covering all other creatures within Far range. All targets within this area must succeed on an Instinct Reaction Roll or be buried in snow and rocks, becoming Vulnerable until they dig themselves out from the debris. For each PC that fails the reaction roll, you gain a Fear."},{"name":"Frozen Scales - Reaction","text":"When a creature makes a successful attack against the Dragon from within Very Close range, they must mark a Stress and become Chilled until their next rest or they clear a Stress. While they are Chilled, they have disadvantage on attack rolls."},{"name":"Momentum - Reaction","text":"When the Dragon makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-arch-necromancer', 'adversary', 'Arch-Necromancer', NULL, 4, '{"description":"A decaying mage adorned in dark, tattered robes.","motives":"Corrupt, decay, flee to fight another day, resurrect","difficulty":"21","thresholds":"33/66","hp":"9","stress":"8","attack":{"modifier":"+6","name":"Necrotic Blast","range":"Far","damage":"4d12+8 mag"},"features":[{"name":"Dance of Death - Action","text":"Mark a Stress to spotlight 1d4 allies. Attacks they make while spotlighted in this way deal half damage, or full damage if you spend a Fear."},{"name":"Beam of Decay - Action","text":"Mark 2 Stress to cause all targets within Far range to make a Strength Reaction Roll. Targets who fail take 2d20+12 magic damage and you gain a Fear. Targets who succeed take half damage. A target who marks 2 or more HP must also mark 2 Stress and becomes Vulnerable until they roll with Hope."},{"name":"Open the Gates of Death - Action","text":"Spend a Fear to summon a Zombie Legion, which appears at Close range and immediately takes the spotlight."},{"name":"Not Today, My Dears - Reaction","text":"When the Necromancer has marked 7 or more of their HP, you can spend a Fear to have them teleport away to a safe location to recover. A PC who succeeds on an Instinct Roll can trace the teleportation magic to their destination."},{"name":"Your Demise is Near - Reaction","text":"Countdown (2d6). When the Necromancer has marked 6 or more of their HP, activate the countdown. When it triggers, deal 2d10+6 direct magic damage to a target within Close range. The Necromancer then clears a number of Stress or HP equal to the number of HP marked by the target from this attack."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-fallen-shock-troop', 'adversary', 'Fallen Shock Troop', NULL, 4, '{"description":"A cursed soul bound to the Fallen’s will.","motives":"Crush, dominate, earn relief, punish","difficulty":"18","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"+2","name":"Cursed Axe","range":"Very Close","damage":"12 phy"},"features":[{"name":"Minion (12) - Passive","text":"The Shock Troop is defeated when they take any damage. For every 12 damage a PC deals to the Shock Troop, defeat an additional Minion within range the attack would succeed against."},{"name":"Aura of Doom - Passive","text":"When a PC marks HP from an attack by the Shock Troop, they lose a Hope."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Fallen Shock Troops within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 12 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-fallen-sorcerer', 'adversary', 'Fallen Sorcerer', NULL, 4, '{"description":"Warped mage bound by the bargains they made in life.","motives":"Acquire, dishearten, dominate, torment","difficulty":"19","thresholds":"26/42","hp":"6","stress":"5","attack":{"modifier":"+4","name":"Corrupted Staff","range":"Far","damage":"4d6+10 mag"},"features":[{"name":"Conflagration - Action","text":"Spend a Fear to unleash an all-consuming firestorm and make an attack against all targets within Close range. Targets the Sorcerer succeeds against take 2d10+6 direct magic damage."},{"name":"Nightmare Tableau - Action","text":"Mark a Stress to trap a target within Far range in a powerful illusion of their worst fears. While trapped, the target is Restrained and Vulnerable until they break free, ending both conditions, with a successful Instinct Roll."},{"name":"Slippery - Reaction","text":"When the Sorcerer takes damage from an attack, they can teleport up to Far range."},{"name":"Shackles of Guilt - Reaction","text":"Countdown (Loop 2d6). When the Sorcerer is in the spotlight for the first time, activate the countdown. When it triggers, all targets within Far range become Vulnerable and must mark a Stress as they relive their greatest regrets. A target can break free from their regret with a successful Presence or Strength Roll. When a PC fails to break free, they lose a Hope."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-fallen-warlord-realm-breaker', 'adversary', 'Fallen Warlord: Realm-Breaker', NULL, 4, '{"description":"A fallen God, wreathed in rage and resentment, bearing millennia of experience in breaking heroes’ spirits.","motives":"Corrupt, dominate, punish, break the weak","difficulty":"20","thresholds":"36/66","hp":"8","stress":"5","attack":{"modifier":"+7","name":"Barbed Whip","range":"Close","damage":"4d8+7 phy"},"features":[{"name":"Relentless (2) - Passive","text":"The Realm-Breaker can be spotlighted up to two times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Firespite Plate Armor - Passive","text":"When the Realm-Breaker takes damage, reduce it by 2d10."},{"name":"Tormenting Lash - Action","text":"Mark a Stress to make a standard attack against all targets within Very Close range. When a target uses armor to reduce damage from this attack, they must mark 2 Armor Slots."},{"name":"All-Consuming Rage - Reaction","text":"Countdown (Decreasing 8). When the Realm-Breaker is in the spotlight for the first time, activate the countdown. When it triggers, create a torrent of incarnate rage that rends flesh from bone. All targets within Far range must make a Presence Reaction Roll. Targets who fail take 2d6+10 direct magic damage. Targets who succeed take half damage. For each HP marked from this damage, summon a Fallen Shock Troop within Very Close range of the target who marked that HP. If the countdown ever decreases its maximum value to 0, the Realm-Breaker marks their remaining HP and all targets within Far range must mark all remaining HP and make a death move."},{"name":"Doombringer - Reaction","text":"When a target marks HP from an attack by the Realm-Breaker, all PCs within Far range of the target must lose a Hope."},{"name":"I Have Never Known Defeat (Phase Change) - Reaction","text":"When the Realm-Breaker marks their last HP, replace them with the Undefeated Champion and immediately spotlight them."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-fallen-warlord-undefeated-champion', 'adversary', 'Fallen Warlord: Undefeated Champion', NULL, 4, '{"description":"That which only the most feared have a chance to fear.","motives":"Dispatch merciless death, punish the defiant, secure victory at any cost","difficulty":"18","thresholds":"35/58","hp":"11","stress":"5","attack":{"modifier":"+8","name":"Heart-Shattering Sword","range":"Very Close","damage":"4d12+13 phy"},"features":[{"name":"Relentless (3) - Passive","text":"The Undefeated Champion can be spotlighted up to three times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Faltering Armor - Passive","text":"When the Undefeated Champion takes damage, reduce it by 1d10."},{"name":"Shattering Strike - Action","text":"Mark a Stress to make a standard attack against all targets within Very Close range. PCs the Champion succeeds against lose a number of Hope equal to the HP they marked from this attack."},{"name":"Endless Legions - Action","text":"Spend a Fear to summon a number of Fallen Shock Troops equal to twice the number of PCs. The Shock Troops appear at Far range."},{"name":"Circle of Defilement - Reaction","text":"Countdown (1d8). When the Undefeated Champion is in the spotlight for the first time, activate the countdown. When it triggers, activate a magical circle covering an area within Far range of the Champion. A target within that area is Vulnerable until they leave the circle. The circle can be removed by dealing Severe damage to the Undefeated Champion."},{"name":"Doombringer - Reaction","text":"When the Undefeated Champion makes a successful attack against a PC, you gain a Fear."},{"name":"Doombringer - Reaction","text":"When a target marks HP from an attack by the Undefeated Champion, all PCs within Far range of the target lose a Hope."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-hallowed-archer', 'adversary', 'Hallowed Archer', NULL, 4, '{"description":"Spirit soldiers with sanctified bows.","motives":"Focus fire, obey, retribution, volley","difficulty":"19","thresholds":"25/45","hp":"3","stress":"2","attack":{"modifier":"+4","name":"Sanctified Longbow","range":"Far","damage":"4d8+8 phy"},"features":[{"name":"Punish the Guilty - Passive","text":"The Archer deals double damage to targets marked Guilty by a High Seraph."},{"name":"Divine Volley - Action","text":"Mark a Stress to make a standard attack against up to three targets."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-hallowed-soldier', 'adversary', 'Hallowed Soldier', NULL, 4, '{"description":"Souls of the faithful, lifted up with divine weaponry.","motives":"Obey, outmaneuver, punish, swarm","difficulty":"18","thresholds":"None","hp":"1","stress":"2","attack":{"modifier":"+2","name":"Sword and Shield","range":"Melee","damage":"10 phy"},"features":[{"name":"Minion (13) - Passive","text":"The Soldier is defeated when they take any damage. For every 13 damage a PC deals to the Soldier, defeat an additional Minion within range the attack would succeed against."},{"name":"Divine Flight - Passive","text":"While the Soldier is flying, spend a Fear to move up to Far range instead of Close range before taking an action."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Hallowed Soldiers within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 10 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-high-seraph', 'adversary', 'High Seraph', NULL, 4, '{"description":"A divine champion, head of a hallowed host of warriors who enforce their god’s will.","motives":"Enforce dogma, fly, pronounce judgment, smite","difficulty":"20","thresholds":"37/70","hp":"7","stress":"5","attack":{"modifier":"+8","name":"Holy Sword","range":"Very Close","damage":"4d10+10 phy"},"features":[{"name":"Relentless (2) - Passive","text":"The Seraph can be spotlighted up to three times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Divine Flight - Passive","text":"While the Seraph is flying, spend a Fear to move up to Far range instead of Close range before taking an action."},{"name":"Judgment - Action","text":"Spend a Fear to make a target Guilty in the eyes of the Seraph’s god until the Seraph is defeated. While Guilty, the target doesn’t gain Hope on a result with Hope. When the Seraph succeeds on a standard attack against a Guilty target, they deal Severe damage instead of their standard damage. The Seraph can only mark one target at a time."},{"name":"God Rays - Action","text":"Mark a Stress to reflect a sliver of divinity as a searing beam of light that hits up to twenty targets within Very Far range. Targets must make a Presence Reaction Roll, with disadvantage if they are marked Guilty. Targets who fail take 4d6+12 magic damage. Targets who succeed take half damage."},{"name":"We Are One - Action","text":"Once per scene, spend a Fear to spotlight all other adversaries within Far range. Attacks they make while spotlighted in this way deal half damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-kraken', 'adversary', 'Kraken', NULL, 4, '{"description":"A legendary beast of the sea, bigger than the largest galleon, with sucker-laden tentacles and a terrifying maw.","motives":"Consume, crush, drown, grapple","difficulty":"20","thresholds":"35/70","hp":"11","stress":"8","attack":{"modifier":"+7","name":"Tentacles","range":"Close","damage":"4d12+10 phy"},"features":[{"name":"Relentless (3) - Passive","text":"The Kraken can be spotlighted up to three times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Many Tentacles - Passive","text":"While the Kraken has 7 or fewer marked HP, they can make their standard attack against two targets within range."},{"name":"Grapple and Drown - Action","text":"Make an attack roll against a target within Close range. On a success, mark a Stress to grab them with a tentacle and drag them beneath the water. The target is Restrained and Vulnerable until they break free with a successful Strength Roll or the Kraken takes Major or greater damage. While Restrained and Vulnerable in this way, a target must mark a Stress when they make an action roll."},{"name":"Boiling Blast - Action","text":"Spend a Fear to spew a line of boiling water at any number of targets in a line up to Far range. All targets must succeed on an Agility Reaction Roll or take 4d6+9 physical damage. If a target marks an Armor Slot to reduce the damage, they must also mark a Stress."},{"name":"Momentum - Reaction","text":"When the Kraken makes a successful attack against a PC, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-oracle-of-doom', 'adversary', 'Oracle of Doom', NULL, 4, '{"description":"A towering immortal and incarnation of fate, cursed to only see bad outcomes.","motives":"Change environment, condemn, dishearten, toss aside","difficulty":"20","thresholds":"38/68","hp":"11","stress":"10","attack":{"modifier":"+8","name":"Psychic Attack","range":"Far","damage":"4d8+9 mag"},"features":[{"name":"Terrifying - Passive","text":"When the Oracle makes a successful attack, all PCs within Far range lose a Hope and you gain a Fear."},{"name":"Walls Closing In - Passive","text":"When a creature rolls a failure while within Very Far range of the Oracle, they must mark a Stress."},{"name":"Pronounce Fate - Action","text":"Spend a Fear to present a target within Far range with a vision of their personal nightmare. The target must make a Knowledge Reaction Roll. On a failure, they lose all Hope and take 2d10+4 direct magic damage. On a success, they take half damage and lose a Hope."},{"name":"Summon Tormentors - Action","text":"Once per day, spend 2 Fear to summon 2d4 Tier 2 or below Minions relevant to one of the PC’s personal nightmares. They appear at Close range relative to that PC."},{"name":"Ominous Knowledge - Reaction","text":"When the Oracle sees a mortal creature, they instantly know one of their personal nightmares."},{"name":"Vengeful Fate - Reaction","text":"When the Oracle marks HP from an attack within Very Close range, you can mark a Stress to knock the attacker back to Far range and deal 2d10+4 physical damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-outer-realms-abomination', 'adversary', 'Outer Realms Abomination', NULL, 4, '{"description":"A chaotic mockery of life, constantly in flux.","motives":"Confuse, demolish, devour, undermine","difficulty":"19","thresholds":"35/71","hp":"7","stress":"5","attack":{"modifier":"+2d4","name":"Massive Pseudopod","range":"Very Close","damage":"4d6+13 mag"},"features":[{"name":"Chaotic Form - Passive","text":"When the Abomination attacks, roll 2d4 and use the result as their attack modifier."},{"name":"Disorienting Presence - Passive","text":"When a target takes damage from the Abomination, they must make an Instinct Reaction Roll. On a failure, they gain disadvantage on their next action roll and you gain a Fear."},{"name":"Reality Quake - Action","text":"Spend a Fear to rattle the edges of reality within Far range of the Abomination. All targets within that area must succeed on a Knowledge Reaction Roll or become Unstuck from reality until the end of the scene. When an Unstuck target spends Hope or marks Armor Slots, HP, or Stress, they must double the amount spent or marked."},{"name":"Fungal Form - Reaction","text":"When the Abomination takes damage, reduce it by 1d20. If the Abomination marks 1 or fewer Hit Points from a successful attack against them, you gain a Fear."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-outer-realms-corrupter', 'adversary', 'Outer Realms Corrupter', NULL, 4, '{"description":"A shifting, formless mass seemingly made of chromatic light.","motives":"Confuse, distract, overwhelm","difficulty":"19","thresholds":"27/47","hp":"4","stress":"3","attack":{"modifier":"+7","name":"Corroding Pseudopod","range":"Very Close","damage":"4d8+5 mag"},"features":[{"name":"Will-Shattering Touch - Passive","text":"When a PC takes damage from the Corrupter, they lose a Hope."},{"name":"Disgorge Reality Flotsam - Action","text":"Mark a Stress to spew partially digested portions of consumed realities at all targets within Close range. Targets must succeed on a Knowledge Reaction Roll or mark 2 Stress."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-outer-realms-thrall', 'adversary', 'Outer Realms Thrall', NULL, 4, '{"description":"A vaguely humanoid form stripped of memory and identity.","motives":"Destroy, disgust, disorient, intimidate","difficulty":"17","thresholds":"None","hp":"1","stress":"1","attack":{"modifier":"+3","name":"Claws and Teeth","range":"Very Close","damage":"11 phy"},"features":[{"name":"Minion (13) - Passive","text":"The Thrall is defeated when they take any damage. For every 13 damage a PC deals to the Thrall, defeat an additional Minion within range the attack would succeed against."},{"name":"Group Attack - Action","text":"Spend a Fear to choose a target and spotlight all Outer Realm Thralls within Close range of them. Those Minions move into Melee range of the target and make one shared attack roll. On a success, they deal 11 physical damage each. Combine this damage."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-volcanic-dragon-ashen-tyrant', 'adversary', 'Volcanic Dragon: Ashen Tyrant', NULL, 4, '{"description":"A legendary, lava-hardened dragon. No enemy has ever had the insolence to wound the dragon so.","motives":"Choke, fly, intimidate, kill or be killed","difficulty":"18","thresholds":"29/55","hp":"8","stress":"5","attack":{"modifier":"+10","name":"Claws and Teeth","range":"Close","damage":"4d12+15 phy"},"features":[{"name":"Relentless (4) - Passive","text":"The Ashen Tyrant can be spotlighted up to four times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Cornered - Passive","text":"Mark a Stress instead of spending a Fear to spotlight the Ashen Tyrant."},{"name":"Ashes to Ashes - Passive","text":"When a PC rolls a failure while within Close range of the Ashen Tyrant, they lose a Hope and you gain a Fear. If the PC can’t lose a Hope, they must mark a HP."},{"name":"Desperate Rampage - Action","text":"Mark 3 Stress to make an attack against all targets within Close range. Targets the Ashen Tyrant succeeds against take 2d20+2 physical damage, are knocked back to Close range of where they were, and must mark a Stress."},{"name":"Ashen Cloud - Action","text":"Spend a Fear to smash the ground beneath an adversary within Far range. While within the ash cloud, a target has disadvantage on action rolls. The ash cloud clears the next time an adversary is spotlighted."},{"name":"Apocalyptic Thrashing - Action","text":"Countdown (1d12). Spend a Fear to activate it. It ticks down when a PC rolls with Fear. When it reaches 0, the Ashen Tyrant thrashes about, causing environmental damage (such as an earthquake, avalanche, or collapsing walls). All targets within Far range must make a Strength Reaction Roll. Targets who fail take 2d10+10 physical damage and are Restrained by the rubble until they break free with a successful Strength Roll. Targets who succeed take half damage. If the Ashen Tyrant is defeated while this countdown is active, trigger the countdown immediately as the destruction caused by their death throes."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-volcanic-dragon-molten-scourge', 'adversary', 'Volcanic Dragon: Molten Scourge', NULL, 4, '{"description":"Engaged by their wounds, the dragon bursts into molten lava.","motives":"Douse with lava, incinerate, repel invaders, respawn","difficulty":"20","thresholds":"30/58","hp":"7","stress":"5","attack":{"modifier":"+9","name":"Lava-Coated Claws","range":"Close","damage":"4d12+4 phy"},"features":[{"name":"Relentless (3) - Passive","text":"Can be spotlighted up to three times per GM turn. Spend Fear as usual to spotlight them."},{"name":"Cracked Scales - Passive","text":"When the Molten Scourge takes damage, roll a number of d6s equal to HP marked. For each result of 4 or higher, you gain a Fear."},{"name":"Shattering Might - Action","text":"Mark a Stress to make an attack against a target within Very Close range. On a success, target takes 4d8+14 physical damage, loses a Hope, and is knocked back to Close range. The Scourge clears a Stress."},{"name":"Eruption - Action","text":"Spend a Fear to erupt lava from beneath the Scourge’s scales, filling area within Very Close range with lava. All targets must make Agility Reaction Roll or take 4d6+6 physical damage and be knocked back to Close range. The area remains lava (6 HP damage on entry or action)."},{"name":"Volcanic Breath - Reaction","text":"When the Scourge takes Major damage, roll d10. On 8+, erupt lava in Very Close range: Agility Reaction Roll or take 2d10+4 physical damage, mark 1d4 Stress, and are Vulnerable until clearing a Stress; success = half damage + mark a Stress.;Lava Splash - Reaction;When the Scourge takes Severe damage from attack within Very Close, molten blood deals 2d10+4 direct physical damage to attacker.;Ashen Vengeance (Phase Change) - Reaction;When the Scourge marks last HP, replace with Ashen Tyrant and immediately spotlight."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-volcanic-dragon-obsidian-predator', 'adversary', 'Volcanic Dragon: Obsidian Predator', NULL, 4, '{"description":"A massive winged creature with obsidian scales and impossibly sharp claws.","motives":"Defend lair, dive-bomb, fly, hunt, intimidate","difficulty":"19","thresholds":"33/65","hp":"6","stress":"5","attack":{"modifier":"+8","name":"Obsidian Claws","range":"Close","damage":"4d10+4 phy"},"features":[{"name":"Relentless (2) - Passive","text":"Can be spotlighted up to two times per GM turn. Spend Fear as usual to spotlight."},{"name":"Flying - Passive","text":"While flying, gains +3 Difficulty."},{"name":"Obsidian Scales - Passive","text":"Resistant to physical damage."},{"name":"Obsidian Tail - Action","text":"Mark a Stress to make attack against all targets within Close range. Success: 4d6+4 physical damage, knocked to Far range and Vulnerable until next roll with Hope."},{"name":"Dive-Bomb - Action","text":"If flying, mark a Stress to choose point within Far range, move there, attack all targets within Very Close range; on success, 2d10+6 physical, mark a Stress, lose a Hope."},{"name":"Erupting Rage (Phase Change) - Reaction","text":"When marks last HP, replace with Molten Scourge and immediately spotlight."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-perfected-zombie', 'adversary', 'Perfected Zombie', NULL, 4, '{"description":"A towering, muscular zombie with magically infused strength and skill.","motives":"Consume, hound, maim, terrify","difficulty":"20","thresholds":"40/70","hp":"9","stress":"4","attack":{"modifier":"+4","name":"Greataxe","range":"Very Close","damage":"4d12+15 phy"},"features":[{"name":"Terrifying - Passive","text":"On successful attack, all PCs in Far range lose Hope and you gain Fear."},{"name":"Fearsome Presence - Passive","text":"PCs can’t spend Hope to use features against the Zombie."},{"name":"Perfect Strike - Action","text":"Mark a Stress to attack all targets within Very Close range; on success, targets are Vulnerable until next rest."},{"name":"Skilled Opportunist - Reaction","text":"When another adversary deals damage to target within Very Close range of Zombie, spend a Fear to add Zombie''s standard attack damage to the damage roll."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
INSERT INTO public.library (id, type, name, domain, tier, data) VALUES ('adversary-zombie-legion', 'adversary', 'Zombie Legion', NULL, 4, '{"description":"A large pack of undead, still powerful despite their rotting flesh.","motives":"Consume brain, shred flesh, surround","difficulty":"17","thresholds":"25/45","hp":"8","stress":"5","attack":{"modifier":"+2","name":"Tentacles","range":"Close","damage":"4d6+10 phy"},"features":[{"name":"Horde (2d6+5) - Passive","text":"When Legion has half or more HP marked, standard attack deals 2d6+5 physical damage instead."},{"name":"Unyielding - Passive","text":"Legion has resistance to physical damage."},{"name":"Relentless (2) - Passive","text":"Legion can be spotlighted up to two times per GM turn. Spend Fear as usual to spotlight."},{"name":"Overwhelm - Reaction","text":"When Legion takes Minor damage from attack within Melee, mark a Stress to make standard attack with advantage against the attacker."}]}'::jsonb) ON CONFLICT (id) DO UPDATE SET data = EXCLUDED.data, name = EXCLUDED.name, domain = EXCLUDED.domain, tier = EXCLUDED.tier;
